import React, { useState, useEffect } from "react";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  // Fetch watchlist data from API
  useEffect(() => {
    fetch("http://localhost:8000/watchlist/")
      .then((response) => response.json())
      .then((data) => {
        setWatchlist(data.watchlist_entries);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  // Function to handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Function to handle form submission for adding watchlist item
  const handleSubmit = (e) => {
    e.preventDefault();
    setWatchlist([...watchlist, formData]);
    setFormData({
      symbol: "",
      price: "",
      target_price: "",
      exp_date: "",
      plan: "",
    });
    setShowModal(false);
  };

  // Function to handle clicking on a watchlist item for editing
  const handleListItemClick = (watchlistItem) => {
    setSelectedWatch(watchlistItem);
    setShowEditModal(true);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="watchlist">
      <div className="header">
        <p>Watchlist</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {watchlist.map((entry, index) => (
            <li
              key={index}
              className="card-content"
              onClick={() => handleListItemClick(entry)}
            >
              <div className="left">
                <p className="symbol">{entry.symbol}</p>
              </div>
              <div className="right">
                <p className="price">Price: ${entry.price}</p>
                <p className="target-price">Target: ${entry.target_price}</p>
                <p className="exp-date">Expiry: {entry.exp_date}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="form-modal">{/* Add form modal content here */}</div>
      )}
      {showEditModal && selectedWatch && (
        <div className="form-modal">{/* Edit form modal content here */}</div>
      )}
    </div>
  );
};

export default Watchlist;
