import React, { useState } from "react";
import "./styles/newwatch.css";
import closeIcon from "./assets/close.png";
import settingIcon from "./assets/setting.png";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editedWatchlist, setEditedWatchlist] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist")
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/watchlist", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add watchlist item");
      }
      const data = await response.json();
      onAdd(data);
      setFormData({
        enter_date: "",
        symbol: "",
        current_price: "",
        target_price: "",
        expiration_date: "",
      });
    } catch (error) {
      console.error("Error adding watchlist item:", error);
    }
  };

  return (
    <div className="new-trade">
      <div className="card">
        <p>Plan</p>
      </div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item) => (
            <li className="list-item" key={item.watch_id}>
              <img
                src={closeIcon}
                alt="Close"
                className="close-icon"
                onClick={() => handleDeleteItem(item.watch_id)}
              />
              <img src={settingIcon} alt="Setting" className="setting-icon" />
              <p>{item.symbol}</p>
              <p>${item.current_price}</p>
              <p>${item.target_price}</p>
            </li>
          ))}
        </ul>
      </div>{" "}
    </div>
  );
};

export default Watchlist;
