import React, { useState, useEffect } from "react";
import "./styles/biggestWinsLosses.css";

const BiggestWinsLosses = ({ recentTrades }) => {
  const [showPositive, setShowPositive] = useState(true);
  const [filteredTrades, setFilteredTrades] = useState([]);

  useEffect(() => {
    filterTrades();
  }, [recentTrades, showPositive]);

  const filterTrades = () => {
    if (recentTrades && recentTrades.length > 0) {
      const positiveTrades = recentTrades.filter((trade) => trade.profit >= 0);
      const negativeTrades = recentTrades.filter((trade) => trade.profit < 0);
      setFilteredTrades(showPositive ? positiveTrades : negativeTrades);
    }
  };

  const toggleProfitDisplay = () => {
    setShowPositive(!showPositive);
  };

  return (
    <div className="wins-lost">
      <div className="card">
        <p>P/L</p>
        <p className="menu" onClick={toggleProfitDisplay}>
          ::
        </p>{" "}
        {/* Toggle display on click */}
      </div>
      <div className="card-list">
        <ul className="cards">
          {filteredTrades.map((trade, index) => {
            const cardItemClasses = `card-item ${
              trade.profit < 0 ? "negative" : ""
            }`;

            return (
              <li className={cardItemClasses} key={index}>
                <div className="card-content">
                  <p>{trade.option_symbol}</p>
                  <p>${trade.profit.toFixed(2)}</p>
                </div>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default BiggestWinsLosses;

