import React, { useState, useEffect } from "react";
import { getCash } from "../api/CashApi";
import { formatTradeDate } from "../func/functions";
import NewCash from "./NewCash";

const Cash = ({ userId }) => {
  const [cashTransactions, setCashTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchCashTransactions = async () => {
      try {
        const data = await getCash(userId);
        setCashTransactions(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchCashTransactions();
  }, [userId]);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleNewCash = (newCash) => {
    setCashTransactions((prevTransactions) => [newCash, ...prevTransactions]);
    setShowModal(false);
  };

  if (loading) {
    return <div>Loading cash transactions...</div>;
  }

  if (error) {
    return <div>Error fetching cash transactions: {error}</div>;
  }

  return (
    <div className="cash-container">
      <div className="header-card">
        <p className="title">Cash Transactions</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="cash-new-btn"
          onClick={handleOpenModal}
        ></i>
      </div>
      <hr />
      <table className="cash-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
            <th>Summary</th>
          </tr>
        </thead>
        <tbody>
          {cashTransactions.map((cash) => (
            <tr key={cash.id}>
              <td>{cash.id}</td>
              <td>{formatTradeDate(cash.transaction_date)}</td>
              <td>{cash.transaction_type}</td>
              <td>${cash.amount.toFixed(2)}</td>
              <td>{cash.transaction_summary}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && (
        <NewCash onClose={handleCloseModal} onNewCash={handleNewCash} />
      )}
    </div>
  );
};

export default Cash;
