import React, { useState, useEffect } from "react";
import "./styles/styles.css";
import { fetchRules } from "./ApiCalls";

const Rules = () => {
  const [rules, setRules] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch rules from the API
  const fetchRulesData = async () => {
    try {
      const rulesData = await fetchRules();
      setRules(rulesData);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // UseEffect hook to fetch rules data when component mounts
  useEffect(() => {
    fetchRulesData();
  }, []);

  return (
    <div className="rules">
      <div className="header">Rules</div>
      {rules.map((rule, index) => (
        <div className="details" key={index}>
          <p className="rule" style={{ fontSize: "12px" }}>
            {index + 1}: {rule.rule}
          </p>
          <br></br>
          {/* <p className="dates">{rule.dates?.split("-").slice(1).join("/")}</p> */}
          {/* <div style={{ width: "20%" }}> */}
          <hr style={{ width: "20%", alignItems: "left" }}></hr>
          {/* </div> */}
        </div>
      ))}
    </div>
  );
};

export default Rules;
