import React, { useEffect, useState } from "react";
import {
  FaChartLine,
  FaPercentage,
  FaMoneyBillWave,
  FaCalendarAlt,
} from "react-icons/fa";
import { fetchDailyPnls } from "../api/DailyPNLApi";

const DailyPNL = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="daily-pnl-container">
      {dailyPNLData.length > 0 ? (
        dailyPNLData.map((pnl, index) => (
          <div key={index} className="daily-pnl-card-container">
            <div className="daily-pnl-card">
              <div className="card-content">
                <FaCalendarAlt className="card-icon" />
                <span className="label">Entry Date:</span>
                <span className="value">{pnl.entry_date}</span>
              </div>
            </div>
            <div className="daily-pnl-card">
              <div className="card-content">
                <FaMoneyBillWave className="card-icon" />
                <span className="label">Open Cash:</span>
                <span className="value">${pnl.open_cash}</span>
              </div>
            </div>
            <div className="daily-pnl-card">
              <div className="card-content">
                <FaMoneyBillWave className="card-icon" />
                <span className="label">Close Cash:</span>
                <span className="value">${pnl.close_cash}</span>
              </div>
            </div>
            <div className="daily-pnl-card">
              <div className="card-content">
                <FaChartLine className="card-icon" />
                <span className="label">Balance:</span>
                <span className="value">${pnl.balance}</span>
              </div>
            </div>
            <div className="daily-pnl-card">
              <div className="card-content">
                <FaPercentage className="card-icon" />
                <span className="label">RoI:</span>
                <span className="value">{pnl.roi}%</span>
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No PNL data available</p>
      )}
    </div>
  );
};

export default DailyPNL;
