import React, { useState, useEffect } from "react";
import { fetchFinancials } from "../api/FinancialAPI";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatDate, formatCash } from "../func/functions";

const SpendingTrendsChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const financials = await fetchFinancials();
      const formattedData = financials.map((entry) => ({
        date: formatDate(entry.entry_date),
        expenses: parseFloat(entry.expenses),
      }));
      setData(formattedData);
    };

    fetchData();
  }, []);

  return (
    <div className="spending-trends-chart">
      <h3>Spending Trends</h3>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={data}>
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip formatter={(value) => formatCash(value)} />
          <Line type="monotone" dataKey="expenses" stroke="#f44336" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default SpendingTrendsChart;
