// export default BiggestWinsLosses;

import React, { useState, useEffect } from "react";
import "./styles/biggestWinsLosses.css";

const BiggestWinsLosses = ({ trades }) => {
  const [biggestWins, setBiggestWins] = useState([]);
  const [biggestLosses, setBiggestLosses] = useState([]);

  // Calculate profits or losses for each trade
  useEffect(() => {
    if (trades && trades.length > 0) {
      // Calculate profits for each trade
      const tradesWithProfits = trades.map((trade) => ({
        ...trade,
        profit: trade.exit_price - trade.entry_price,
      }));

      // Sort trades by profits (positive) or losses (negative)
      const sortedTrades = tradesWithProfits
        .slice()
        .sort((a, b) => b.profit - a.profit);
      const wins = sortedTrades.filter((trade) => trade.profit > 0).slice(0, 5); // Get top 5 biggest wins
      const losses = sortedTrades
        .filter((trade) => trade.profit < 0)
        .slice(0, 5); // Get top 5 biggest losses
      setBiggestWins(wins);
      setBiggestLosses(losses);
    }
  }, [trades]);

  // Function to render a card with specified color and height
  const renderCard = (symbol, profit, color) => (
    <div
      className="card"
      style={{ backgroundColor: color, height: "10px", width: "100%" }}
    >
      <p>{symbol}</p>
      <p className="profit">{profit}</p>
    </div>
  );

  return (
    <div className="wins-lost">
      <div className="wins-container">
        <h2>Biggest Wins</h2>
        {biggestWins.length > 0 ? (
          biggestWins.map((trade) =>
            renderCard(trade.symbol, `+$${trade.profit}`, "green")
          )
        ) : (
          <p>No data available</p>
        )}
      </div>
      <div className="losses-container">
        <h2>Biggest Losses</h2>
        {biggestLosses.length > 0 ? (
          biggestLosses.map((trade) =>
            renderCard(trade.symbol, `-$${Math.abs(trade.profit)}`, "red")
          )
        ) : (
          <p>No data available</p>
        )}
      </div>
    </div>
  );
};

export default BiggestWinsLosses;
