import React, { useState } from "react";
import { formatDate } from "../func/functions";

const UpdateMisc = ({ misc, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    category: misc.category || "",
    entry_date: misc.entry_date || "",
    description: misc.description || "",
  });
  const [showUpdateButton, setShowUpdateButton] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    setShowUpdateButton(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
    setShowUpdateButton(false);
  };

  return (
    <div className="modal-container">
      <div className="modal-content">
        <div className="modal-header">
          <h2>
            {misc.category.charAt(0).toUpperCase() + misc.category.slice(1)}
          </h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>
        <div className="modal-body">
          <div className="misc-details">
            <div className="watchlist-header">
              <p className="no-id">
                N<sup>o</sup>: {`000${misc.id}`.slice(-5)}
              </p>
              <p className="entry-date">{formatDate(watchlist.entry_date)}</p>
            </div>
            <hr />
            <div className="form-group">
              <label>
                Category:
                <input
                  type="text"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  required
                />
              </label>
            </div>
            <div className="form-group">
              <label>
                Entry Date:
                <input
                  type="date"
                  name="entry_date"
                  value={formData.entry_date}
                  onChange={handleChange}
                  required
                />
              </label>
            </div>
            <div className="form-group">
              <label>
                Description:
                <input
                  type="text"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                />
              </label>
            </div>
            {showUpdateButton && (
              <button onClick={handleSubmit} className="update-button">
                Update
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateMisc;
