import React, { useEffect, useState } from "react";
import DailyPNL from "../controllers/pnl/DailyPNL";
import NewDailyPNL from "../controllers/pnl/NewDailyPNL"; // Import the NewDailyPNL component

const Home = ({ onClose }) => {
  const [userData, setUserData] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const storedUserData = localStorage.getItem("user_data");
    if (storedUserData) {
      const parsedUserData = JSON.parse(storedUserData);
      setUserData(parsedUserData);
    }
  }, []);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleNewDailyPNL = (newDailyPNL) => {
    console.log("New daily PNL entry:", newDailyPNL);
  };

  return (
    <div className="home">
      <div className="home-dash">
        <div className="header-card">
          {userData && (
            <p className="title">
              Welcome {userData.fname || userData.username}!
            </p>
          )}
          <i
            className="btn btn-primary fa-solid fa-plus"
            id="new-pnl-btn"
            onClick={handleOpenModal}
          ></i>
        </div>
      </div>

      <div>
        <DailyPNL />
      </div>
      {showModal && (
        <NewDailyPNL
          onClose={handleCloseModal}
          onNewDailyPNL={handleNewDailyPNL}
        />
      )}
    </div>
  );
};

export default Home;
