import React, { useState, useEffect } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { getCash } from "../api/CashApi";
import {
  AreaChart,
  Area,
  Line,
  XAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatCash } from "../func/functions";

const EQTCurve = () => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [equitySummary, setEquitySummary] = useState(null);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const cashData = await getCash();
        const trades = await fetchTrades();

        if (cashData && trades.length > 0) {
          const startingEquity = parseFloat(cashData.initial_cash || 0);
          const sortedTrades = trades.sort(
            (a, b) => new Date(a.close_date) - new Date(b.close_date)
          );
          const formattedData = formatChartData(sortedTrades, startingEquity);
          setChartData(formattedData);
          setEquitySummary(calculateEquitySummary(formattedData));
        } else {
          setError("No trade or cash data available");
        }
      } catch (error) {
        setError("Error fetching trade or cash data");
        console.error("Error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchChartData();
  }, []);

  const formatChartData = (trades, startingEquity) => {
    let cumulativeEquity = startingEquity;
    const idealIncrement =
      (startingEquity * 3 - startingEquity) / trades.length; // Slope for ideal line

    return trades.map((trade, index) => {
      cumulativeEquity += parseFloat(trade.profit_loss || 0) * 100;
      const idealEquity = startingEquity + idealIncrement * (index + 1);

      return {
        tradeCount: index + 1,
        equity: cumulativeEquity,
        idealEquity: idealEquity,
        above: cumulativeEquity > idealEquity ? cumulativeEquity : idealEquity,
        below: cumulativeEquity < idealEquity ? cumulativeEquity : idealEquity,
      };
    });
  };

  const calculateEquitySummary = (data) => {
    if (!data.length)
      return {
        balance: 0,
        percentageChange: 0,
        latestDate: "All Time",
        pnl: 0,
      };

    const initialBalance = data[0].equity;
    const finalBalance = data[data.length - 1].equity;
    const pnl = finalBalance - initialBalance;
    const percentageChange =
      ((finalBalance - initialBalance) / initialBalance) * 100;

    return {
      balance: finalBalance,
      percentageChange: isNaN(percentageChange) ? 0 : percentageChange,
      latestDate: "All Time", // Display "All Time"
      pnl: pnl,
    };
  };

  return (
    <div className="equity-curve-chart-container">
      <div className="summary-section">
        <p className="equity-cash">{formatCash(equitySummary?.balance || 0)}</p>
        <p className="equity-roi">
          {equitySummary
            ? `${formatCash(
                equitySummary.pnl || 0
              )} (${equitySummary.percentageChange.toFixed(2)}%)`
            : "Loading..."}
        </p>
        <p className="equity-date">{equitySummary?.latestDate}</p>
      </div>

      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={400}>
          <AreaChart data={chartData}>
            <XAxis dataKey="tradeCount" tick={false} axisLine={false} />
            <Tooltip
              cursor={false}
              content={({ payload }) => {
                if (payload && payload.length) {
                  const { tradeCount, equity } = payload[0].payload;
                  return (
                    <div className="tooltip-content">
                      <p>{formatCash(equity)}</p>
                      <p className="trade-count">Trade #{tradeCount}</p>
                    </div>
                  );
                }
                return null;
              }}
            />

            {/* Ideal Line at ~45 degrees */}
            <Line
              type="monotone"
              dataKey="idealEquity"
              stroke="green"
              strokeWidth={2}
              dot={false}
            />

            {/* Equity Curve Line */}
            <Line
              type="monotone"
              dataKey="equity"
              stroke="purple"
              strokeWidth={2}
              dot={{ r: 3 }}
            />

            {/* Area Above Ideal Line */}
            <Area
              type="monotone"
              dataKey="above"
              stroke="none"
              fill="pink"
              fillOpacity={0.5}
            />

            {/* Area Below Ideal Line */}
            <Area
              type="monotone"
              dataKey="below"
              stroke="none"
              fill="lightblue"
              fillOpacity={0.5}
            />
          </AreaChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default EQTCurve;
