// ApiCalls.js

export const fetchTrades = async () => {
  try {
    const response = await fetch("http://localhost:8000/trades/");
    const data = await response.json();
    const transformedTrades = data.trades.map((trade) => ({
      id: trade[0],
      symbol: trade[1],
      optionType: trade[2],
      strikePrice: trade[3],
      expDate: trade[4],
      entryPrice: trade[5],
      exitPrice: trade[6],
      contracts: trade[7],
      entryDate: trade[8],
      closeDate: trade[9],
      profitLoss: trade[10],
    }));
    return transformedTrades;
  } catch (error) {
    console.error("Error fetching trades:", error);
    throw error;
  }
};

