import React, { useState } from "react";

const NewTrade = ({ onTradeSubmit }) => {
  const [formData, setFormData] = useState({
    option_symbol: "",
    option_type: "",
    strike_price: "",
    expiration_date: "",
    entry_price: "",
    contracts: "",
    underlying_stock_price: "",
    implied_volatility: "",
    rationale: "",
    feedback: "",
    outcome: "",
    trade_date: "", // Add trade_date to form data
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    // Convert expiration date to ISO 8601 format
    const newValue =
      name === "expiration_date" ? new Date(value).toISOString() : value;
    setFormData((prevData) => ({
      ...prevData,
      [name]: newValue,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Populate the trade_date with current timestamp
    const currentDate = new Date().toISOString();
    setFormData((prevData) => ({
      ...prevData,
      trade_date: currentDate,
    }));
    // Call the callback function to submit the form data
    onTradeSubmit(formData);
    // Clear the form after submission
    setFormData({
      option_symbol: "",
      option_type: "",
      strike_price: "",
      expiration_date: "",
      entry_price: "",
      contracts: "",
      underlying_stock_price: "",
      implied_volatility: "",
      rationale: "",
      feedback: "",
      outcome: "",
      trade_date: "", // Clear trade_date after submission
    });
  };

  return (
    <div className="grid-item-card-right">
      <div className="card">
        <p>New Trade</p>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="option_symbol"
          placeholder="Option Symbol"
          value={formData.option_symbol}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="option_type"
          placeholder="Option Type"
          value={formData.option_type}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="strike_price"
          placeholder="Strike Price"
          value={formData.strike_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="expiration_date"
          //   placeholder="Expiration Date (MM/DD/YYYY)"
          value={formData.expiration_date}
          onChange={handleChange}
          pattern="\d{1,2}/\d{1,2}/\d{4}"
          required
        />
        <input
          type="text"
          name="entry_price"
          placeholder="Entry Price"
          value={formData.entry_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contracts"
          placeholder="Contracts"
          value={formData.contracts}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="underlying_stock_price"
          placeholder="Underlying Stock Price"
          value={formData.underlying_stock_price}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="implied_volatility"
          placeholder="Implied Volatility"
          value={formData.implied_volatility}
          onChange={handleChange}
          required
        />
        <textarea
          name="rationale"
          placeholder="Rationale"
          value={formData.rationale}
          onChange={handleChange}
          required
        />
        <textarea
          name="feedback"
          placeholder="Feedback"
          value={formData.feedback}
          onChange={handleChange}
          required
        />
        <textarea
          name="outcome"
          placeholder="Outcome"
          value={formData.outcome}
          onChange={handleChange}
          required
        />
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default NewTrade;
