import React, { useState } from "react";
import { registerUser } from "../api/UserAPI";
import { decodeJWT } from "../func/functions";

const Register = () => {
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [accountType, setAccountType] = useState("");
  const [error, setError] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const userData = {
        fname,
        lname,
        username,
        email,
        password,
        phone_number: phoneNumber,
        account_type: accountType,
      };

      // Register the user
      const response = await registerUser(userData);

      // Extract and decode the access token
      const { access_token } = response;
      localStorage.setItem("access_token", access_token);

      const decodedToken = decodeJWT(access_token);
      localStorage.setItem("user_data", JSON.stringify(decodedToken));

      // Alert message
      alert("User has been added and data has been stored.");

      // Clear form fields
      setFname("");
      setLname("");
      setUsername("");
      setEmail("");
      setPassword("");
      setPhoneNumber("");
      setAccountType("");
    } catch (err) {
      setError(err.message || "Error registering user");
      alert("Error registering user: " + (err.message || "Unknown error"));
    }
  };

  return (
    <form onSubmit={handleRegister} className="auth-form">
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        placeholder="First Name"
        value={fname}
        onChange={(e) => setFname(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Last Name"
        value={lname}
        onChange={(e) => setLname(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Phone Number"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
        required
      />
      <select
        value={accountType}
        onChange={(e) => setAccountType(e.target.value)}
        required
      >
        <option value="" disabled>
          Select Account Type
        </option>
        <option value="personal">Personal</option>
        <option value="business">Business</option>
      </select>
      <button type="submit" className="btn">
        Register
      </button>
    </form>
  );
};

export default Register;
