import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatCash, formatDate } from "../func/functions";

const InvestmentChart = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("1W");
  const [chartData, setChartData] = useState([]);
  const [investmentSummary, setInvestmentSummary] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        if (data && data.length > 0) {
          setDailyPNLData(data);
          setInvestmentSummary(calculateInvestmentSummary(data));
        } else {
          setError("No data available");
        }
      } catch (error) {
        setError("Error fetching data");
        console.error("Error fetching daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDailyPNLData();
  }, []);

  useEffect(() => {
    if (dailyPNLData.length > 0) {
      switch (view) {
        case "1W":
          setChartData(formatChartData(getCurrentWeek(dailyPNLData)));
          break;
        case "1M":
          setChartData(formatChartData(getCurrentMonth(dailyPNLData)));
          break;
        case "3M":
          setChartData(formatChartData(getLastThreeMonths(dailyPNLData)));
          break;
        default:
          setChartData(formatChartData(dailyPNLData));
          break;
      }
    }
  }, [dailyPNLData, view]);

  const calculateInvestmentSummary = (data) => {
    const initialBalance = parseFloat(data[0]?.close_cash || 0);
    const finalBalance = parseFloat(data[data.length - 1]?.close_cash || 0);
    const percentageChange =
      ((finalBalance - initialBalance) / initialBalance) * 100;

    return {
      balance: finalBalance,
      percentageChange: isNaN(percentageChange) ? 0 : percentageChange,
    };
  };

  const formatChartData = (data) => {
    // Sort data by entry_date in ascending order
    const sortedData = [...data].sort(
      (a, b) => new Date(a.entry_date) - new Date(b.entry_date)
    );

    return sortedData.map((entry) => ({
      date: formatDate(entry.entry_date),
      cumulativeBalance: parseFloat(entry.close_cash || 0), // Use close_cash for cumulative balance
    }));
  };

  const getCurrentWeek = (data) => {
    const today = new Date();
    const startOfWeek = new Date(today);
    startOfWeek.setDate(today.getDate() - today.getDay());
    return data.filter(
      (entry) =>
        new Date(entry.entry_date) >= startOfWeek &&
        new Date(entry.entry_date) <= today
    );
  };

  const getCurrentMonth = (data) => {
    const today = new Date();
    const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
    return data.filter(
      (entry) =>
        new Date(entry.entry_date) >= startOfMonth &&
        new Date(entry.entry_date) <= today
    );
  };

  const getLastThreeMonths = (data) => {
    const today = new Date();
    const startOfThreeMonths = new Date(today);
    startOfThreeMonths.setMonth(today.getMonth() - 2);
    return data.filter(
      (entry) =>
        new Date(entry.entry_date) >= startOfThreeMonths &&
        new Date(entry.entry_date) <= today
    );
  };

  return (
    <div className="investment-chart-container">
      <div className="summary-section">
        <h2>Investing Summary</h2>
        <p>Current Balance: {formatCash(investmentSummary?.balance || 0)}</p>
        <p>
          Change:{" "}
          {investmentSummary
            ? `${investmentSummary.percentageChange.toFixed(2)}% All time`
            : "Loading..."}
        </p>
      </div>

      <div className="view-buttons">
        <button
          className={`view-button ${view === "1D" ? "active" : ""}`}
          onClick={() => setView("1W")}
        >
          1W
        </button>
        <button
          className={`view-button ${view === "1W" ? "active" : ""}`}
          onClick={() => setView("1M")}
        >
          1M
        </button>
        <button
          className={`view-button ${view === "1M" ? "active" : ""}`}
          onClick={() => setView("3M")}
        >
          3M
        </button>
      </div>

      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData}>
            <XAxis dataKey="date" />
            <YAxis dataKey="cumulativeBalance" />
            <Tooltip
              formatter={(value) => formatCash(value)}
              labelFormatter={(label) => `Date: ${label}`}
            />
            <Line
              type="monotone"
              dataKey="cumulativeBalance"
              stroke="#4a90e2"
              strokeWidth={2}
              dot={{ r: 2 }}
            />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default InvestmentChart;
