import React, { useEffect, useState } from "react";
import { fetchSummary } from "./ApiCalls";
import { successModal, unsuccessModal } from "./Utility";

const Summary = () => {
  const [summary, setSummary] = useState([]);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedSummary, setSelectedSummary] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    category: "",
    summary: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (showEditModal) {
      setSelectedSummary({ ...selectedSummary, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const fetchSummaryData = async () => {
    try {
      const summaryData = await fetchSummary();
      if (Array.isArray(summaryData)) {
        setSummary(summaryData);
        successModal();
      } else {
        throw new Error("Fetched data is not an array");
      }
    } catch (error) {
      setError(error.message);
      unsuccessModal(error.message);
    }
  };

  useEffect(() => {
    fetchSummaryData();
  }, []);

  const handleItemClick = (summary) => {
    setSelectedSummary(summary);
    setShowEditModal(true);
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    const [year, month, day] = dateStr.split("-");
    return `${year}-${month}-${day}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedData = {
        ...formData,
        dates: formatDate(formData.dates),
      };
      const response = await fetch("http://localhost:8000/summary/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formattedData),
      });
      if (!response.ok) {
        throw new Error("Failed to add Summary");
      }
      successModal();
      setShowModal(false);
      setFormData({
        dates: "",
        category: "",
        summary: "",
      });
      fetchSummaryData();
    } catch (error) {
      console.log("Error adding Summary: ", error);
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedSummary = {
        id: selectedSummary.id,
        dates: formatDate(selectedSummary.dates),
        category: selectedSummary.category,
        summary: selectedSummary.summary,
      };
      const response = await fetch(
        `http://localhost:8000/summary/${selectedSummary.id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formattedSummary),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update Summary");
      }
      successModal();
      setShowEditModal(false);
      fetchSummaryData();
    } catch (error) {
      console.log("Error updating Summary: ", error);
    }
  };
  // Get the day of the week and formatted date
  const getFormattedDate = (dateString) => {
    const [year, month, day] = dateString.split("-");
    const date = new Date(year, month - 1, day); // Month is 0-indexed in JS Date
    const daysOfWeek = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    const dayName = daysOfWeek[date.getDay()];
    const formattedDate = `${month}/${day}`; // Format month/day
    return `${dayName}, ${formattedDate}`;
  };

  return (
    <div className="summary">
      <div className="header">
        <p>Summary</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {summary.map((item, index) => (
            <li
              key={index}
              className="card-content"
              onClick={() => handleItemClick(item)}
            >
              <div className="left">
                <p className="summary">{item.summary}</p>
                <p className="category">{item.category}</p>
                <p className="dates">
                  {item.dates?.split("-").slice(1).join("/")}
                </p>{" "}
                <p className="dates">{getFormattedDate(item.dates)}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <p>Summary</p>
              <span className="close" onClick={() => setShowModal(false)}>
                &times;
              </span>
            </div>
            <form className="new-trade-form" onSubmit={handleSubmit}>
              <label htmlFor="dates">Date:</label>
              <input
                type="date"
                name="dates"
                value={formData.dates}
                onChange={handleChange}
              />

              <label htmlFor="category">Category:</label>
              <input
                type="text"
                name="category"
                value={formData.category}
                onChange={handleChange}
              />

              <label htmlFor="summary">Summary:</label>
              <input
                type="text"
                name="summary"
                value={formData.summary}
                onChange={handleChange}
              />

              <button type="submit">Add</button>
            </form>
          </div>
        </div>
      )}
      {showEditModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              {selectedSummary.category}
              <hr></hr>
              {/* {selectedSummary.dates?.split("-").slice(1).join("/")} */}
              <span className="close" onClick={() => setShowEditModal(false)}>
                &times;
              </span>
            </div>
            <form className="edit-trade-form" onSubmit={handleEditSubmit}>
              {/* <label htmlFor="dates">Date:</label>
              <input
                type="date"
                name="dates"
                value={selectedSummary.dates}
                onChange={handleChange}
              />

              <label htmlFor="category">Category:</label>
              <input
                type="text"
                name="category"
                value={selectedSummary.category}
                onChange={handleChange}
              /> */}

              <label htmlFor="summary">Summary:</label>
              <input
                type="text"
                name="summary"
                value={selectedSummary.summary}
                onChange={handleChange}
              />

              <button type="submit">Update</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Summary;
