// InitialCash.js

import React, { useState } from "react";
import "./styles/initialCash.css";

const InitialCash = ({ onInitialCashSubmit }) => {
  const [initialCash, setInitialCash] = useState("");
  const [displayedInitialCash, setDisplayedInitialCash] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ensure initial cash is a valid number
    if (!isNaN(parseFloat(initialCash))) {
      // Pass initial cash to parent component
      onInitialCashSubmit(parseFloat(initialCash));
      // Display initial cash above input field
      setDisplayedInitialCash(parseFloat(initialCash));
    }
  };

  return (
    <div className="initial-cash-container">
      {displayedInitialCash !== null && (
        <p className="initial-cash-display">
          Initial Cash: ${displayedInitialCash.toFixed(2)}
        </p>
      )}
      <form className="initial" onSubmit={handleSubmit}>
        <label htmlFor="initial-cash">Initial Cash:</label>
        <input
          id="initial-cash"
          type="number"
          placeholder="Enter initial cash amount"
          value={initialCash}
          onChange={(e) => setInitialCash(e.target.value)}
          required
        />

        <button type="submit" value="Add" />
      </form>
    </div>
  );
};

export default InitialCash;
