// UpdateMisc.js

import React, { useState } from "react";
import {
  formatDate,
  splitText,
  handleTextAreaResize,
  toggleEdit,
  handleSubmit,
} from "../func/functions";

const UpdateMisc = ({ misc, onClose, onSave, handleDelete }) => {
  const [formData, setFormData] = useState({
    category: misc.category || "",
    entry_date: misc.entry_date || "",
    description: misc.description || "",
  });
  const [isEditable, setIsEditable] = useState(false);
  const [showUpdateButton, setShowUpdateButton] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    setShowUpdateButton(true);
  };

  return (
    <div className="modal-container">
      <div className="modal-content">
        <div className="modal-header">
          <h2>
            {misc.category.charAt(0).toUpperCase() + misc.category.slice(1)}
          </h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>
        <div className="modal-body">
          <div className="misc-details">
            <div className="misc-header">
              <p className="no-id">
                N<sup>o</sup>: {`000${misc.id}`.slice(-5)}
              </p>
              <p className="entry-date">{formatDate(misc.entry_date)}</p>
            </div>
            <hr />
            <div className="misc-description">
              {isEditable ? (
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  onInput={handleTextAreaResize}
                  rows="1"
                  style={{ width: "100%", overflow: "hidden" }}
                />
              ) : (
                splitText(misc.description)
              )}
            </div>
            {showUpdateButton && (
              <button
                onClick={(e) =>
                  handleSubmit(
                    e,
                    onSave,
                    formData,
                    setShowUpdateButton,
                    setIsEditable
                  )
                }
                className="update-button"
              >
                Update
              </button>
            )}
          </div>
        </div>
        <hr />
        <div className="modal-footer">
          <div className="misc-item-footer">
            <span
              onClick={() => toggleEdit(setIsEditable, setShowUpdateButton)}
              className="settings-icon"
            >
              <i className="fa-solid fa-cog"></i>
            </span>
            <span
              onClick={(e) => {
                e.stopPropagation();
                handleDelete(misc.id);
              }}
              className="delete-icon"
            >
              <i className="fa-solid fa-trash"></i>
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateMisc;

