import React, { useState } from "react";
import { createCash } from "../api/CashApi";

const NewCash = ({ onClose, onNewCash }) => {
  const [formData, setFormData] = useState({
    user_id: "",
    transaction_type: "deposit",
    transaction_date: "",
    amount: "",
    transaction_summary: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newCash = await createCash({
        ...formData,
        amount: parseFloat(formData.amount),
        user_id: parseInt(formData.user_id),
      });
      onNewCash(newCash);
      alert("Cash transaction submitted successfully");
      onClose();
    } catch (error) {
      console.error("Error submitting cash transaction:", error);
      alert("Error submitting cash transaction");
    }
  };

  return (
    <div className="modal">
      <div className="new-cash-container">
        <div className="header-card">
          <p className="title">New Cash Transaction</p>
          <p className="close-btn" onClick={onClose}>
            &times;
          </p>
        </div>
        <hr />
        <form onSubmit={handleSubmit} className="form-container">
          <label>
            <input
              type="text"
              placeholder="User ID"
              name="user_id"
              value={formData.user_id}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            <select
              name="transaction_type"
              value={formData.transaction_type}
              onChange={handleChange}
              required
            >
              <option value="deposit">Deposit</option>
              <option value="withdrawal">Withdrawal</option>
            </select>
          </label>
          <br />
          <label>
            <input
              type="date"
              placeholder="Transaction Date"
              name="transaction_date"
              value={formData.transaction_date}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            <input
              type="text"
              placeholder="Amount"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            <input
              type="text"
              placeholder="Summary"
              name="transaction_summary"
              value={formData.transaction_summary}
              onChange={handleChange}
            />
          </label>
          <br />
          <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
};

export default NewCash;
