import React, { useEffect, useState } from "react";
import { FaChartLine, FaPercentage, FaMoneyBillWave } from "react-icons/fa";
import { getDailyPNL } from "../api/DailyPNLApi"; // Ensure this is the correct path

const DailyPNL = () => {
  const [dailyPNLData, setDailyPNLData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await getDailyPNL();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  return (
    <div className="daily-pnl-container">
      <div className="daily-pnl-card-container">
        {/* Daily PNL Card */}
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Net P/L:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `$${dailyPNLData?.net_pnl || "N/A"}`}
            </span>
          </div>
        </div>

        {/* Balance Card */}
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Balance:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `$${dailyPNLData?.balance || "N/A"}`}
            </span>
          </div>
        </div>

        {/* RoI Card */}
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">RoI:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `${dailyPNLData?.roi || "N/A"}%`}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DailyPNL;
