import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  const formatMonthYear = (dateStr) => {
    if (!dateStr) return "";
    const [year, month] = dateStr.split("-");
    return `${month}/${year}`;
  };

  const aggregateData = (interval, numDaysPerInterval) => {
    let aggregatedBalances = [];
    let labels = [];
    let intervalSum = 0;
    let intervalStartDate = "";

    dailyPNLData.forEach((day, index) => {
      intervalSum += parseFloat(day.balance);

      if (index % numDaysPerInterval === 0) {
        intervalStartDate = day.entry_date;
      }

      const isEndOfInterval = (index + 1) % numDaysPerInterval === 0;
      const isLastDataPoint = index === dailyPNLData.length - 1;

      if (isEndOfInterval || isLastDataPoint) {
        aggregatedBalances.push(intervalSum);
        labels.push(
          interval === "monthly"
            ? formatMonthYear(intervalStartDate)
            : formatDate(intervalStartDate)
        );
        intervalSum = 0;
      }
    });

    return { balances: aggregatedBalances, labels };
  };

  const calculateBars = () => {
    switch (view) {
      case "daily":
        return aggregateData("daily", 1);
      case "weekly":
        return aggregateData("weekly", 5);
      case "monthly":
        return aggregateData("monthly", 20); // Approximate 20 trading days in a month
      default:
        return { balances: [], labels: [] };
    }
  };

  const { balances, labels } = calculateBars();
  const maxBalance = Math.max(...balances);
  const threshold = maxBalance * 1.2;

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "daily" ? "active" : ""}`}
            onClick={() => setView("daily")}
          >
            Daily
          </button>
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            Weekly
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            Monthly
          </button>
        </div>
      </div>

      <hr />

      <div className="bars-container">
        {balances.map((balance, index) => {
          const percentage = (Math.abs(balance) / threshold) * 100;
          const isPositive = balance >= 0;
          return (
            <div key={index} className="bar-wrapper">
              <span className="bar-label">{labels[index]}</span>
              <div
                className={`bar ${isPositive ? "positive" : "negative"}`}
                style={{
                  width: `${percentage}%`,
                }}
              >
                <span className="bar-value">{formatCash(balance)}</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;

