import React, { useEffect, useRef, useState } from "react";
import { fetchTrades } from "./ApiCalls";

const Graph = () => {
  const chartRef = useRef(null);
  const [loading, setLoading] = useState(true);
  const [trades, setTrades] = useState([]);
  const [profitLoss, setProfitLoss] = useState({
    "1D": 0,
    "1W": 0,
    "1M": 0,
    "3M": 0,
    "1Y": 0,
    YTD: 0,
    Max: 0,
  });

  useEffect(() => {
    fetchTrades()
      .then((data) => {
        setTrades(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching trades:", error);
      });
  }, []);

  // Calculate profit/loss for different time periods
  useEffect(() => {
    if (!loading && trades.length > 0) {
      const now = new Date();
      const oneDay = 1000 * 60 * 60 * 24;
      const oneWeek = oneDay * 7;
      const oneMonth = oneDay * 30;
      const threeMonths = oneMonth * 3;
      const oneYear = oneDay * 365;

      let profitLossForLastDay = 0;
      let profitLossForLastWeek = 0;
      let profitLossForLastMonth = 0;
      let profitLossForLast3Months = 0;
      let profitLossForLastYear = 0;
      let profitLossYearToDate = 0;
      let profitLossForMaxPeriod = 0;

      trades.forEach((trade) => {
        const closeDate = new Date(trade.closeDate);
        const timeDiff = now - closeDate;
        const contracts = trade.contracts;

        profitLossForMaxPeriod += trade.profitLoss * contracts * 100;

        if (timeDiff <= oneDay) {
          profitLossForLastDay += trade.profitLoss * contracts * 100;
        }
        if (timeDiff <= oneWeek) {
          profitLossForLastWeek += trade.profitLoss * contracts * 100;
        }
        if (timeDiff <= oneMonth) {
          profitLossForLastMonth += trade.profitLoss * 100;
        }
        if (timeDiff <= threeMonths) {
          profitLossForLast3Months += trade.profitLoss * 100;
        }
        if (timeDiff <= oneYear) {
          profitLossForLastYear += trade.profitLoss * 100;
        }
        if (closeDate.getFullYear() === now.getFullYear()) {
          profitLossYearToDate += trade.profitLoss * 100;
        }
      });

      setProfitLoss({
        "1D": profitLossForLastDay,
        "1W": profitLossForLastWeek,
        "1M": profitLossForLastMonth,
        "3M": profitLossForLast3Months,
        "1Y": profitLossForLastYear,
        YTD: profitLossYearToDate,
        Max: profitLossForMaxPeriod,
      });
    }
  }, [loading, trades]);

  useEffect(() => {
    if (!loading && trades.length > 0) {
      const ctx = chartRef.current.getContext("2d");
      const backgroundColors = Object.values(profitLoss).map((value) =>
        value >= 0 ? "#bed1be" : "rgb(255, 204, 204)"
      );
      const myChart = new window.Chart(ctx, {
        type: "bar",
        data: {
          labels: ["1D", "1W", "1M", "3M", "1Y", "YTD", "Max"],
          datasets: [
            {
              label: "Profit/Loss",
              data: Object.values(profitLoss),
              backgroundColor: backgroundColors,
              barThickness: 40,
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: false,
            },
          },
          scales: {
            x: {
              grid: {
                display: false,
              },
            },
            y: {
              display: true,
              title: {
                display: true,
                text: "Profit/Loss",
              },
              grid: {
                color: (context) =>
                  context.tick.value === 0
                    ? "rgba(0, 0, 0, 1)"
                    : "rgba(0, 0, 0, 0)",
                lineWidth: 0.5,
              },
              ticks: {
                callback: function (value, index, values) {
                  return value === 0 ? value : "";
                },
              },
            },
          },
        },
      });
      return () => {
        if (myChart) {
          myChart.destroy();
        }
      };
    }
  }, [loading, trades, profitLoss]);
  return (
    <div className="graph">
      {loading ? (
        <p style={{ fontSize: "10px" }}>Loading...</p>
      ) : (
        <canvas ref={chartRef} />
      )}
    </div>
  );
};

export default Graph;
