import React, { useState, useEffect } from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  const [accountBalance, setAccountBalance] = useState(0);
  const [dailyPL, setDailyPL] = useState(0);
  const [periodPL, setPeriodPL] = useState(0);
  const [timePeriod, setTimePeriod] = useState("1D");

  const handleTimePeriodChange = (period) => {
    setTimePeriod(period);
  };

  useEffect(() => {
    const calculateBalanceAndPL = () => {
      let balance = 86;
      let dailyProfitLoss = 0;
      let periodProfitLoss = 0;

      trades.forEach((trade) => {
        const entryPrice = parseFloat(trade.entry_price);
        const exitPrice = parseFloat(trade.exit_price);
        const contracts = trade.contracts;

        if (!isNaN(exitPrice)) {
          const profit = (exitPrice - entryPrice) * contracts * 100;
          balance += profit;
          dailyProfitLoss += profit;

          const tradeCloseDate = new Date(trade.close_date);
          const periodStartDate = new Date();
          switch (timePeriod) {
            case "1D":
              periodStartDate.setDate(periodStartDate.getDate() - 1);
              break;
            case "1W":
              periodStartDate.setDate(periodStartDate.getDate() - 7);
              break;
            case "1M":
              periodStartDate.setMonth(periodStartDate.getMonth() - 1);
              break;
            case "3M":
              periodStartDate.setMonth(periodStartDate.getMonth() - 3);
              break;
            case "YTD":
              periodStartDate.setMonth(0);
              periodStartDate.setDate(1);
              break;
            case "1Y":
              periodStartDate.setFullYear(periodStartDate.getFullYear() - 1);
              break;
            case "Max":
              const periodPL = profit;
              periodProfitLoss = Math.max(periodProfitLoss, periodPL);
              break;
            default:
              break;
          }
          if (tradeCloseDate >= periodStartDate) {
            periodProfitLoss += profit;
          }
        }
      });

      setAccountBalance(balance);
      setDailyPL(dailyProfitLoss);
      setPeriodPL(periodProfitLoss);
    };

    calculateBalanceAndPL();
  }, [trades, timePeriod]);

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">{accountBalance.toFixed(2)}</p>
      </div>
      <div className="card">
        <p>{`${timePeriod} P/L`}</p>
        <p className="cash">{periodPL.toFixed(2)}</p>
      </div>
      <AccountGraph
        trades={trades}
        onTimePeriodChange={handleTimePeriodChange}
        periodPL={periodPL}
      />{" "}
      {/* Pass periodPL as a prop */}
    </div>
  );
};

export default Account;

