import React, { useState, useEffect } from "react";
import "./styles/plan.css";

const Plan = () => {
  const [strategies, setStrategies] = useState([]); // eslint-disable-next-line
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    fetchStrategies();
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist")
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  const fetchStrategies = async () => {
    try {
      const response = await fetch("http://localhost:8000/strategies/");
      if (!response.ok) {
        throw new Error("Failed to fetch strategies");
      }
      const data = await response.json();
      setStrategies(data);
    } catch (error) {
      console.error("Error fetching strategies:", error.message);
    }
  };

  return (
    <div className="plan">
      <div className="header-card">
        <p>Plan</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          // onClick={handlePlusIconClick}
        ></i>
      </div>
      <div className="plan-list-container">
        <ul className="cards">
          {strategies.map((strategy) => (
            <li key={strategy.strategy_id} className="list-item">
              <div className="card-summary">
                <div className="category">
                  <h3>Plan:</h3>
                  <p>{strategy.plan}</p>
                </div>
                <div className="category">
                  <h3>Position:</h3>
                  <p>{strategy.position_sizing_rules}</p>
                </div>
                <div className="category">
                  <h3>Outcome:</h3>
                  <p>{strategy.outcome}</p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Plan;
