import React, { useState, useEffect } from "react";
import Chart from "chart.js/auto";

const Graph = () => {
  const [profitsData, setProfitsData] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/Graph/")
      .then((response) => response.json())
      .then((data) => setProfitsData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  useEffect(() => {
    if (profitsData.length > 0) {
      renderChart();
    }
  }, [profitsData]);

  const renderChart = () => {
    const labels = profitsData.map((data) => data.label);
    const data = profitsData.map((data) => data.profit);

    const ctx = document.getElementById("myChart");
    new Chart(ctx, {
      type: "bar",
      data: {
        labels: labels,
        datasets: [
          {
            label: "Profit/Loss",
            data: data,
            backgroundColor: data.map((profit) =>
              profit < 0 ? "rgba(255, 99, 132, 0.2)" : "rgba(75, 192, 192, 0.2)"
            ),
            borderWidth: 1,
            barThickness: 40,
          },
        ],
      },
      options: {
        plugins: {
          legend: {
            display: false,
          },
        },
        scales: {
          x: {
            grid: {
              display: false,
            },
          },
          y: {
            display: true,
            title: {
              display: true,
              text: "Profit/Loss",
            },
            grid: {
              color: (context) =>
                context.tick.value === 0
                  ? "rgba(0, 0, 0, 1)"
                  : "rgba(0, 0, 0, 0)",
              lineWidth: 0.5,
            },
            ticks: {
              callback: function (value, index, values) {
                return value === 0 ? value : "";
              },
            },
          },
        },
      },
    });
  };

  return <canvas id="myChart"></canvas>;
};

export default Graph;
