// CashApi.js
const API_BASE_URL = "http://localhost:8000";

async function createCash(cashAmount) {
  const userId = localStorage.getItem("sub"); // Get the user_id from local storage
  const token = localStorage.getItem("token"); // Get the token

  const response = await fetch("/api/cash/", {
    method: "POST",
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      user_id: userId, // Send user_id in the request payload
      initial_cash: cashAmount,
      entry_date: new Date().toISOString().split("T")[0], // Format the date
    }),
  });

  const data = await response.json();
  if (response.ok) {
    console.log("Cash added successfully:", data);
  } else {
    console.error("Error adding cash:", data);
  }
}

// Function to fetch cash entries (optional, can be implemented as needed)
export const getCash = async () => {
  const token = localStorage.getItem("access_token");

  const response = await fetch(`${API_BASE_URL}/cash/`, {
    method: "GET",
    headers: {
      Authorization: `Bearer ${token}`, // Include the JWT token for authorization
    },
  });

  if (!response.ok) {
    throw new Error("Error fetching cash data");
  }

  return await response.json();
};
