import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const ProgressInvestmentChart = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartData, setChartData] = useState([]);
  const [investmentSummary, setInvestmentSummary] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        if (data && data.length > 0) {
          const sortedData = data.sort(
            (a, b) => new Date(a.entry_date) - new Date(b.entry_date)
          );
          setChartData(formatChartData(sortedData));
          setInvestmentSummary(calculateInvestmentSummary(sortedData));
        } else {
          setError("No data available");
        }
      } catch (error) {
        setError("Error fetching data");
        console.error("Error fetching daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDailyPNLData();
  }, []);

  const calculateInvestmentSummary = (data) => {
    if (!data.length)
      return { balance: 0, percentageChange: 0, latestDate: "", pnl: 0 };

    const initialBalance = parseFloat(data[0]?.open_cash || 0);
    const finalBalance = parseFloat(data[data.length - 1]?.close_cash || 0);
    const pnl = finalBalance - initialBalance;
    const percentageChange =
      ((finalBalance - initialBalance) / initialBalance) * 100;
    const latestDate = formatDate(data[data.length - 1]?.entry_date);

    return {
      balance: finalBalance,
      percentageChange: isNaN(percentageChange) ? 0 : percentageChange,
      latestDate: latestDate,
      pnl: pnl,
    };
  };

  const formatChartData = (data) => {
    return data.map((entry) => ({
      date: formatDate(entry.entry_date),
      pnl: parseFloat(entry.close_cash || 0) - parseFloat(entry.open_cash || 0),
    }));
  };

  return (
    <div className="progress-investment-chart-container">
      <div className="summary-section">
        <p className="investment-cash">
          {formatCash(investmentSummary?.balance || 0)}
        </p>
        <p className="investment-roi">
          {investmentSummary
            ? `${formatCash(
                investmentSummary.pnl || 0
              )} (${investmentSummary.percentageChange.toFixed(2)}%)`
            : "Loading..."}
        </p>
        <p className="investment-date">
          {investmentSummary?.latestDate || "Loading..."}
        </p>
      </div>

      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <div className="progress-bar-container">
          {chartData.map((day, index) => (
            <div key={index} className="progress-bar-row">
              <span className="date-label">{day.date}</span>
              <div
                className="progress-bar-day"
                style={{
                  backgroundColor: day.pnl >= 0 ? "#4CAF50" : "#F44336",
                  width: `${
                    Math.abs(day.pnl / investmentSummary.balance) * 100
                  }%`,
                  marginLeft:
                    day.pnl >= 0
                      ? "50%"
                      : `${
                          50 -
                          Math.abs(day.pnl / investmentSummary.balance) * 50
                        }%`,
                }}
                title={`${day.date}: ${formatCash(day.pnl)}`}
              ></div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProgressInvestmentChart;
