import React, { useState, useEffect } from "react";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  // Fetch watchlist data from API
  useEffect(() => {
    const fetchWatchlist = async () => {
      try {
        const response = await fetch("http://localhost:8000/watchlist/");
        const data = await response.json();
        setWatchlist(data.watchlist_entries);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchWatchlist();
  }, []);

  // Function to handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
  // Function to handle form submission for adding watchlist item
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/watchlist/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add watchlist item");
      }
      const data = await response.json();
      // Update the watchlist state with the new item
      setWatchlist((prevWatchlist) => [...prevWatchlist, data]);
      // Clear the form data
      setFormData({
        dates: "",
        symbol: "",
        price: "",
        target_price: "",
        exp_date: "",
        plan: "",
      });
      // Close the modal
      setShowModal(false);
    } catch (err) {
      setError(err.message);
    }
  };

  // Function to handle clicking on a watchlist item for editing
  const handleListItemClick = (watchlistItem) => {
    setSelectedWatch(watchlistItem);
    setShowEditModal(true);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;
  return (
    <div className="watchlist">
      <div className="header">
        <p>Watchlist</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {watchlist.map((entry, index) => (
            <li
              key={index}
              className="card-content"
              onClick={() => handleListItemClick(entry)}
            >
              <div className="left">
                <p className="symbol">{entry.symbol}</p>
              </div>
              <div className="right">
                <p className="date">
                  Date: {new Date(entry.date).toLocaleDateString()}
                </p>
                <p className="price">Price: ${entry.price}</p>
                <p className="target-price">Target: ${entry.target_price}</p>
                <p className="exp-date">
                  Expiry: {new Date(entry.exp_date).toLocaleDateString()}
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <p>Add to Watchlist</p>
              <span className="close" onClick={() => setShowModal(false)}>
                &times;
              </span>
            </div>
            <form className="new-watchlist-form" onSubmit={handleSubmit}>
              <label htmlFor="date">Date:</label>
              <input
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
              />

              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
              />

              <label htmlFor="price">Price:</label>
              <input
                type="text"
                name="price"
                value={formData.price}
                onChange={handleChange}
              />

              <label htmlFor="target_price">Target Price:</label>
              <input
                type="text"
                name="target_price"
                value={formData.target_price}
                onChange={handleChange}
              />

              <label htmlFor="exp_date">Exp. Date:</label>
              <input
                type="date"
                name="exp_date"
                value={formData.exp_date}
                onChange={handleChange}
              />

              <label htmlFor="plan">Plan:</label>
              <textarea
                name="plan"
                value={formData.plan}
                onChange={handleChange}
              ></textarea>

              <button type="submit">Add</button>
            </form>
          </div>
        </div>
      )}
      {showEditModal && selectedWatch && (
        <div className="form-modal">{/* Edit form modal content here */}</div>
      )}
    </div>
  );
};

export default Watchlist;
