import React, { useState, useEffect } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { getCash } from "../api/CashApi";
import {
  Line,
  XAxis,
  Tooltip,
  ResponsiveContainer,
  LineChart,
  YAxis,
  ReferenceArea,
} from "recharts";
import { formatCash, formatDate } from "../func/functions";

const EQTCurve = () => {
  const [chartData, setChartData] = useState([]);
  const [shadedRegions, setShadedRegions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAndFormatData = async () => {
      try {
        const cashData = await getCash();
        const trades = await fetchTrades();

        if (cashData && trades && trades.length > 0) {
          const startingEquity = parseFloat(cashData.initial_cash || 0);
          const formattedData = formatChartData(trades, startingEquity);
          setChartData(formattedData);
          setShadedRegions(calculateShadedRegions(formattedData));
        } else {
          setError("No trade or cash data available");
        }
      } catch (error) {
        setError("Error fetching trade or cash data");
        console.error("Error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAndFormatData();
  }, []);

  const formatChartData = (trades, startingEquity) => {
    let cumulativeEquity = startingEquity;
    const idealIncrement =
      (startingEquity * 3 - startingEquity) / trades.length;

    const sortedTrades = trades.sort(
      (a, b) => new Date(a.close_date) - new Date(b.close_date)
    );

    return sortedTrades.map((trade, index) => {
      cumulativeEquity += parseFloat(trade.profit_loss || 0) * 100;
      const idealEquity = startingEquity + idealIncrement * (index + 1);

      return {
        date: formatDate(trade.close_date),
        equity: cumulativeEquity,
        idealEquity: idealEquity,
      };
    });
  };

  const calculateShadedRegions = (data) => {
    const regions = [];
    let startShadeDate = null;
    let shadeType = null;

    data.forEach((point, index) => {
      if (index === 0) return;

      const prevPoint = data[index - 1];
      const { date, equity, idealEquity } = point;

      if (prevPoint.equity < prevPoint.idealEquity && equity >= idealEquity) {
        if (shadeType === "below") {
          regions.push({
            start: startShadeDate,
            end: prevPoint.date,
            type: "below",
            color: "red",
          });
        }
        startShadeDate = prevPoint.date;
        shadeType = "above";
      } else if (
        prevPoint.equity > prevPoint.idealEquity &&
        equity <= idealEquity
      ) {
        if (shadeType === "above") {
          regions.push({
            start: startShadeDate,
            end: prevPoint.date,
            type: "above",
            color: "green",
          });
        }
        startShadeDate = prevPoint.date;
        shadeType = "below";
      }
    });

    if (shadeType) {
      regions.push({
        start: startShadeDate,
        end: data[data.length - 1].date,
        type: shadeType,
      });
    }

    return regions;
  };

  // Find index of date in chartData
  const getDateIndex = (date) => {
    return chartData.findIndex((item) => item.date === date);
  };

  return (
    <div className="equity-curve-chart-container">
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData} style={{ background: "transparent" }}>
            <XAxis dataKey="date" tick={false} axisLine={false} />
            <YAxis hide={true} />

            <Tooltip
              cursor={false}
              content={({ payload }) => {
                if (payload && payload.length) {
                  const { date, equity } = payload[0].payload;
                  return (
                    <div className="tooltip-content">
                      <p>{formatCash(equity)}</p>
                      <p className="trade-date">{date}</p>
                    </div>
                  );
                }
                return null;
              }}
            />

            {shadedRegions.map((region, index) => (
              <ReferenceArea
                key={index}
                x1={getDateIndex(region.start)}
                x2={getDateIndex(region.end)}
                y1="dataMin"
                y2="dataMax"
                fill={region.type === "above" ? "lightblue" : "lightcoral"}
                fillOpacity={0.2}
              />
            ))}

            <Line
              type="monotone"
              dataKey="idealEquity"
              stroke="#9AD3BC"
              strokeWidth={2}
              dot={false}
              name="Ideal Equity"
            />

            <Line
              type="monotone"
              dataKey="equity"
              stroke="#4A90E2"
              strokeWidth={2}
              dot={false}
              name="Equity Curve"
            />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default EQTCurve;

