import React, { useState, useEffect } from "react";
import "./styles/styles.css";
import { fetchRules } from "./ApiCalls";

const Rules = () => {
  const [rules, setRules] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch rules from the API
  const fetchRulesData = async () => {
    try {
      const rulesData = await fetchRules();
      setRules(rulesData);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  // UseEffect hook to fetch rules data when component mounts
  useEffect(() => {
    fetchRulesData();
  }, []);

  return (
    <div className="rules">
      <div className="header">Rules</div>
      <div className="list">
        <ul className="item">
          {rules.map((rule, index) => (
            <li key={index} className="card-content">
              <div className="left">
                <p className="summary">{rule.rule}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Rules;
