// trading-app/trading-frontend/src/components/controllers/trades/TradeTable.js

import React, { useEffect, useState } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { formatTradeDate, formatTicker } from "../func/functions"; // Importing the functions

const TradeTable = () => {
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getTrades = async () => {
      try {
        const data = await fetchTrades();
        setTrades(data);
        console.log(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getTrades();
  }, []); // Run only once on component mount

  if (loading) {
    return <div>Loading trades...</div>;
  }

  if (error) {
    return <div>Error fetching trades: {error}</div>;
  }

  return (
    <div>
      <h2>Trades</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Symbol</th>
            <th>Entry Date</th>
            <th>Entry Price</th>
            <th>Contracts</th>
            <th>Principal</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((trade) => (
            <tr key={trade.id}>
              <td>{trade.id}</td>
              <td>
                {trade.symbol}
                {trade.strike_price} {trade.option_type}
                {formatTradeDate(trade.exp_date)}
              </td>
              <td>{formatTradeDate(trade.entry_date)}</td>{" "}
              <td>${trade.entry_price}</td>
              <td>{trade.contracts}</td>
              <td>{(trade.principal * 100).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TradeTable;
