import React, { useState, useEffect } from "react";
import { formatDate } from "./Utility";
import "./styles/watchlist.css";
import closeIcon from "./assets/close.png";
import settingIcon from "./assets/setting.png";
import NewWatch from "./NewWatch";
import Plan from "./Plan";
import NewPlan from "./NewPlan";

const Watchlist = () => {
  // State variables
  const [watchlist, setWatchlist] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editedWatchlist, setEditedWatchlist] = useState(null);
  const [showPlanModal, setShowPlanModal] = useState(false);
  const [planFormData, setPlanFormData] = useState({
    watch_id: "",
    plan: "",
    outcome: "",
    author: "",
    max_loss_tolerance: 0,
    position_sizing_rules: "",
    tags: "",
    status: "Active",
    version: 0,
  });

  // Fetch watchlist data from API
  useEffect(() => {
    fetch("http://localhost:8000/watchlist")
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  // Handle click on a card item
  const handleCardItemClick = (item) => {
    setSelectedItem(item);
    setShowPlanModal(true);
  };

  // Handle close of the plan modal
  const handleClosePlanModal = () => {
    setSelectedItem(null);
    setShowPlanModal(false);
  };

  // Handle input change in the plan form
  const handlePlanInputChange = (e) => {
    const { name, value } = e.target;
    setPlanFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle submission of the plan form
  const handlePlanFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/plans", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(planFormData),
      });
      if (!response.ok) {
        throw new Error("Failed to add plan");
      }
      // Close the modal and fetch updated watchlist data
      handleClosePlanModal();
      fetch("http://localhost:8000/watchlist")
        .then((response) => response.json())
        .then((data) => setWatchlist(data))
        .catch((error) => console.error("Error fetching watchlist:", error));
    } catch (error) {
      console.error("Error adding plan:", error);
      handleClosePlanModal();
    }
  };

  // Handle click on settings icon
  const handleSettingsClick = (item) => {
    setSelectedItem(item);
    setEditedWatchlist(item);
    setShowModal(true);
  };

  // Handle close of the modal
  const handleCloseModal = () => {
    setSelectedItem(null);
    setShowModal(false);
  };

  // Handle input change in the form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedWatchlist((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle submission of the form
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/watchlist/${editedWatchlist.watch_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(editedWatchlist),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update watchlist item");
      }
      handleCloseModal();
      fetch("http://localhost:8000/watchlist")
        .then((response) => response.json())
        .then((data) => setWatchlist(data))
        .catch((error) => console.error("Error fetching watchlist:", error));
    } catch (error) {
      console.error("Error updating watchlist item:", error);
      handleCloseModal();
    }
  };

  return (
    <div className="watchlist-container">
      <div className="new-watch-container">
        <NewWatch watchlist={watchlist} />
      </div>
      <div className="watchlist">
        <div className="header-card">
          <p>Watchlist</p>
        </div>
        <div className="list-container">
          <ul className="cards">
            {watchlist.map((item) => (
              <li
                className="list-item"
                key={item.watch_id}
                onClick={() => handleCardItemClick(item)}
              >
                <div className="left">
                  <div>{item.watch_id}</div>
                  <div className="symbol">{item.symbol}</div>
                </div>
                <div className="right">
                  <div className="details">
                    Date: {formatDate(item.enter_date)}
                  </div>
                  <div className="details">Price: ${item.current_price}</div>
                  <div>Target: ${item.target_price}</div>
                  <div>Exp date: {formatDate(item.expiration_date)}</div>
                  <img
                    src={closeIcon}
                    alt="Close"
                    className="close-icon"
                    onClick={() => handleDeleteItem(item.watch_id)}
                  />
                  <img
                    src={settingIcon}
                    alt="Setting"
                    className="setting-icon"
                    onClick={() => handleSettingsClick(item)}
                  />
                </div>
              </li>
            ))}
          </ul>
        </div>
        {showModal && (
          <div className="modal" onClick={handleCloseModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <span className="close" onClick={handleCloseModal}>
                &times;
              </span>
              <div className="card">
                <h1>{editedWatchlist.symbol}</h1>
              </div>
              {editedWatchlist && (
                <div>
                  <form className="watch-form" onSubmit={handleFormSubmit}>
                    <label htmlFor="current_price">Current Price:</label>
                    <input
                      type="text"
                      name="current_price"
                      value={editedWatchlist.current_price}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="target_price">Target Price:</label>
                    <input
                      type="text"
                      name="target_price"
                      value={editedWatchlist.target_price}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="expiration_date">Exp. date:</label>
                    <input
                      type="date"
                      name="expiration_date"
                      value={editedWatchlist.expiration_date}
                      onChange={handleInputChange}
                      required
                    />
                    <input type="submit" value="Submit" />
                  </form>
                </div>
              )}
            </div>
          </div>
        )}
        {showPlanModal && (
          <div className="modal" onClick={handleClosePlanModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <span className="close" onClick={handleClosePlanModal}>
                &times;
              </span>
              <div className="card">
                <h1>{selectedItem.symbol}</h1>
              </div>
              <div>
                <form className="plan-form" onSubmit={handlePlanFormSubmit}>
                  <label htmlFor="watch_id">Watch ID:</label>
                  <input
                    type="text"
                    name="watch_id"
                    value={selectedItem.watch_id}
                    onChange={handlePlanInputChange}
                    disabled
                  />
                  <label htmlFor="plan">Plan:</label>
                  <input
                    type="text"
                    name="plan"
                    value={planFormData.plan}
                    onChange={handlePlanInputChange}
                    required
                  />
                  <label htmlFor="outcome">Outcome:</label>
                  <input
                    type="text"
                    name="outcome"
                    value={planFormData.outcome}
                    onChange={handlePlanInputChange}
                    required
                  />
                  <label htmlFor="author">Author:</label>
                  <input
                    type="text"
                    name="author"
                    value={planFormData.author}
                    onChange={handlePlanInputChange}
                    required
                  />
                  <label htmlFor="max_loss_tolerance">
                    Max Loss Tolerance:
                  </label>
                  <input
                    type="number"
                    name="max_loss_tolerance"
                    value={planFormData.max_loss_tolerance}
                    onChange={handlePlanInputChange}
                    required
                  />
                  <label htmlFor="position_sizing_rules">
                    Position Sizing Rules:
                  </label>
                  <input
                    type="text"
                    name="position_sizing_rules"
                    value={planFormData.position_sizing_rules}
                    onChange={handlePlanInputChange}
                    required
                  />
                  <label htmlFor="tags">Tags:</label>
                  <input
                    type="text"
                    name="tags"
                    value={planFormData.tags}
                    onChange={handlePlanInputChange}
                    required
                  />
                  <input type="submit" value="Submit" />
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
      <div className="new-plan-container">
        <Plan />
      </div>
    </div>
  );
};

export default Watchlist;
