import React, { useState, useEffect } from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  const [accountBalance, setAccountBalance] = useState(0);
  const [dailyPL, setDailyPL] = useState(0);
  const [periodPL, setPeriodPL] = useState(0);
  const [timePeriod, setTimePeriod] = useState("1D");
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleTimePeriodChange = (event) => {
    setTimePeriod(event.target.textContent);
    setDropdownOpen(false);
  };

  useEffect(() => {
    const calculateBalanceAndPL = () => {
      let balance = 0;
      let dailyProfitLoss = 0;
      let periodProfitLoss = 0;

      trades.forEach((trade) => {
        const entryPrice = parseFloat(trade.entry_price);
        const exitPrice = parseFloat(trade.exit_price);
        const contracts = trade.contracts;

        if (!isNaN(exitPrice)) {
          const profit = (exitPrice - entryPrice) * contracts * 100;
          balance += profit;
          dailyProfitLoss += profit;
        }

        const tradeCloseDate = new Date(trade.close_date);

        if (isWithinTimePeriod(tradeCloseDate, timePeriod, new Date())) {
          periodProfitLoss += calculateProfit(trade);
        }
      });

      setAccountBalance(balance);
      setDailyPL(dailyProfitLoss);
      setPeriodPL(periodProfitLoss);
    };

    const calculateProfit = (trade) => {
      const entryPrice = parseFloat(trade.entry_price);
      const exitPrice = parseFloat(trade.exit_price);
      const contracts = trade.contracts;
      if (!isNaN(exitPrice)) {
        return (exitPrice - entryPrice) * contracts * 100;
      }
      return 0;
    };

    const isWithinTimePeriod = (tradeCloseDate, period, today) => {
      const periodStartDate = new Date();

      switch (period) {
        case "1D":
          periodStartDate.setDate(today.getDate() - 1);
          break;
        case "1W":
          periodStartDate.setDate(today.getDate() - 7);
          break;
        case "1M":
          periodStartDate.setMonth(today.getMonth() - 1);
          break;
        case "3M":
          periodStartDate.setMonth(today.getMonth() - 3);
          break;
        case "YTD":
          periodStartDate.setFullYear(today.getFullYear(), 0, 1);
          break;
        case "1Y":
          periodStartDate.setFullYear(today.getFullYear() - 1);
          break;
        case "Max":
          // Implement Max logic here if needed
          break;
        default:
          break;
      }

      return period === "Max" || tradeCloseDate >= periodStartDate;
    };

    calculateBalanceAndPL();
  }, [trades, timePeriod]);

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">$ {accountBalance.toFixed(2)}</p>
      </div>
      <div className="card">
        <div
          className="dropdown"
          onMouseEnter={() => setDropdownOpen(true)}
          onMouseLeave={() => setDropdownOpen(false)}
        >
          <button className="dropbtn"> {timePeriod} </button>
          {dropdownOpen && (
            <div className="dropdown-content">
              <a href="#" onClick={handleTimePeriodChange}>
                1D
              </a>
              <a href="#" onClick={handleTimePeriodChange}>
                1W
              </a>
              <a href="#" onClick={handleTimePeriodChange}>
                1M
              </a>
              <a href="#" onClick={handleTimePeriodChange}>
                3M
              </a>
              <a href="#" onClick={handleTimePeriodChange}>
                YTD
              </a>
              <a href="#" onClick={handleTimePeriodChange}>
                1Y
              </a>
              <a href="#" onClick={handleTimePeriodChange}>
                Max
              </a>
            </div>
          )}
        </div>
        <p className="cash">$ {periodPL.toFixed(2)}</p>
      </div>
      <AccountGraph
        trades={trades}
        onTimePeriodChange={handleTimePeriodChange}
        periodPL={periodPL}
        timePeriod={timePeriod}
      />
    </div>
  );
};

export default Account;

