import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import { FaArrowUp, FaArrowDown } from "react-icons/fa";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash } from "../func/functions";

const Calender = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [modalDetails, setModalDetails] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  const aggregateByMonth = () => {
    const monthlyData = {};

    // Ensure data is sorted by date (ascending)
    dailyPNLData.sort(
      (a, b) => new Date(a.entry_date) - new Date(b.entry_date)
    );
    console.log("Sorted dailyPNLData:", dailyPNLData);

    dailyPNLData.forEach((entry) => {
      const date = new Date(entry.entry_date);
      const monthKey = `${date.getFullYear()}-${date.getMonth() + 1}`;

      console.log(`Processing entry for ${monthKey}:`, entry);

      // Initialize the monthly data if not already present
      if (!monthlyData[monthKey]) {
        monthlyData[monthKey] = {
          initial_open_cash: entry.open_cash, // Set once from the first entry
          final_close_cash: entry.close_cash, // Will be updated to the last entry's close_cash
          balance: parseFloat(entry.balance),
          roi: 0,
          entries: [], // Track entries
        };
        console.log(`Initialized data for ${monthKey}:`, monthlyData[monthKey]);
      } else {
        monthlyData[monthKey].balance += parseFloat(entry.balance);
        // console.log(
        //   `Updated balance for ${monthKey}:`,
        //   monthlyData[monthKey].balance
        // );

        monthlyData[monthKey].final_close_cash = entry.close_cash;
        // console.log(
        //   `Updated final_close_cash for ${monthKey}:`,
        //   monthlyData[monthKey].final_close_cash
        // );
      }

      monthlyData[monthKey].entries.push(entry);
      // console.log(
      //   `Tracked entry for ${monthKey}:`,
      //   monthlyData[monthKey].entries
      // );
    });

    // Calculate ROI for each month after looping
    Object.keys(monthlyData).forEach((monthKey) => {
      const monthData = monthlyData[monthKey];
      const { entries } = monthData;

      // console.log(`Calculating ROI for ${monthKey}:`);
      if (entries.length > 0) {
        monthData.initial_open_cash = entries[0].open_cash;
        // console.log(
        //   `Initial open cash for ${monthKey}:`,
        //   monthData.initial_open_cash
        // );

        monthData.final_close_cash = entries[entries.length - 1].close_cash;
        // console.log(
        //   `Final close cash for ${monthKey}:`,
        //   monthData.final_close_cash
        // );
      } else {
        // console.warn(`No entries found for ${monthKey}. Cannot calculate ROI.`);
      }

      monthData.roi = (
        ((parseFloat(monthData.final_close_cash) -
          parseFloat(monthData.initial_open_cash)) /
          parseFloat(monthData.initial_open_cash)) *
        100
      ).toFixed(2);

      // console.log(`ROI for ${monthKey}:`, monthData.roi);
    });

    // console.log("Aggregated Monthly Data:", monthlyData);
    return monthlyData;
  };

  // Aggregates PNL data by year
  const aggregateByYear = () => {
    const yearlyData = {};

    dailyPNLData.forEach((entry) => {
      const date = new Date(entry.entry_date);
      const yearKey = date.getFullYear();

      if (!yearlyData[yearKey]) {
        yearlyData[yearKey] = {
          initial_open_cash: entry.open_cash,
          final_close_cash: entry.close_cash,
          balance: parseFloat(entry.balance),
          roi: 0,
          entries: [],
        };
      } else {
        yearlyData[yearKey].balance += parseFloat(entry.balance);
        yearlyData[yearKey].final_close_cash = entry.close_cash;
      }
      yearlyData[yearKey].entries.push(entry);
    });

    Object.keys(yearlyData).forEach((yearKey) => {
      const yearData = yearlyData[yearKey];
      const { entries } = yearData;

      if (entries.length > 0) {
        yearData.initial_open_cash = entries[0].open_cash;
        // console.log(
        //   "Initial Open Cash for year",
        //   yearKey,
        //   ":",
        //   yearData.initial_open_cash
        // );

        yearData.final_close_cash = entries[entries.length - 1].close_cash;
        // console.log(
        //   "Final Close Cash for year",
        //   yearKey,
        //   ":",
        //   yearData.final_close_cash
        // );
      }
      yearData.roi = (
        ((parseFloat(yearData.final_close_cash) -
          parseFloat(yearData.initial_open_cash)) /
          parseFloat(yearData.initial_open_cash)) *
        100
      ).toFixed(2);
      // console.log("ROI for year", yearKey, ":", yearData.roi);
    });

    // console.log("Yearly Data Aggregated:", yearlyData);
    return yearlyData;
  };

  const tileContent = ({ date, view }) => {
    const formattedDate = date.toISOString().split("T")[0];

    if (view === "month") {
      const pnlEntry = dailyPNLData.find(
        (pnl) => pnl.entry_date === formattedDate
      );

      if (pnlEntry) {
        const icon =
          pnlEntry.open_cash < pnlEntry.close_cash ? (
            <FaArrowUp style={{ color: "#4caf50" }} />
          ) : (
            <FaArrowDown style={{ color: "red" }} />
          );

        return (
          <div className="pnl-tile">
            <div>{icon}</div>
            <div>{formatCash(pnlEntry.balance)}</div>
            <div>{pnlEntry.roi}%</div>
          </div>
        );
      }
    } else if (view === "year") {
      const monthlyData = aggregateByMonth();
      const monthKey = `${date.getFullYear()}-${date.getMonth() + 1}`;
      const monthlyEntry = monthlyData[monthKey];

      if (monthlyEntry) {
        const icon =
          monthlyEntry.open_cash < monthlyEntry.close_cash ? (
            <FaArrowUp style={{ color: "#4caf50" }} />
          ) : (
            <FaArrowDown style={{ color: "red" }} />
          );

        return (
          <div className="pnl-tile">
            <div>{icon}</div>
            <div>{formatCash(monthlyEntry.balance)}</div>
            <div>{monthlyEntry.roi}%</div>
          </div>
        );
      }
    } else if (view === "decade") {
      const yearlyData = aggregateByYear();
      const yearKey = date.getFullYear();
      const yearlyEntry = yearlyData[yearKey];

      if (yearlyEntry) {
        const icon =
          yearlyEntry.open_cash < yearlyEntry.close_cash ? (
            <FaArrowUp style={{ color: "#4caf50" }} />
          ) : (
            <FaArrowDown style={{ color: "red" }} />
          );

        return (
          <div className="pnl-tile">
            <div>{icon}</div>
            <div>{formatCash(yearlyEntry.balance)}</div>
            <div>{yearlyEntry.roi}%</div>
          </div>
        );
      }
    }

    return null;
  };

  const handleTileClick = (date, view) => {
    let modalDetails = null;

    if (view === "month") {
      const formattedDate = date.toISOString().split("T")[0];
      const pnlEntry = dailyPNLData.find(
        (pnl) => pnl.entry_date === formattedDate
      );

      if (pnlEntry) {
        const formattedDateForDisplay = `${
          date.getMonth() + 1
        }/${date.getDate()}`;
        modalDetails = { date: formattedDateForDisplay, ...pnlEntry };
      }
    } else if (view === "year") {
      const monthKey = `${date.getFullYear()}-${date.getMonth() + 1}`;
      const monthlyData = aggregateByMonth();
      const monthlyEntry = monthlyData[monthKey];

      if (monthlyEntry) {
        modalDetails = { date: monthKey, ...monthlyEntry };
      }
    } else if (view === "decade") {
      const yearKey = date.getFullYear();
      const yearlyData = aggregateByYear();
      const yearlyEntry = yearlyData[yearKey];

      if (yearlyEntry) {
        modalDetails = { date: yearKey, ...yearlyEntry };
      }
    }

    if (modalDetails) {
      setModalDetails(modalDetails);
    }
  };

  const handleCloseModal = () => {
    setModalDetails(null);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="calendar-container">
      <Calendar
        onChange={setSelectedDate}
        value={selectedDate}
        tileContent={tileContent}
        onClickDay={(date) => handleTileClick(date, "month")}
        onActiveStartDateChange={({ activeStartDate, view }) =>
          handleTileClick(activeStartDate, view)
        }
      />
      {modalDetails && (
        <div className="modal-container">
          <div className="modal-content">
            <div className="modal-header">
              <h2>{modalDetails.date}</h2>
              <button className="close-button" onClick={handleCloseModal}>
                &times;
              </button>
            </div>
            <div className="modal-body">
              <p>
                <strong>Balance:</strong> {formatCash(modalDetails.balance)}
              </p>
              <p>
                <strong>RoI:</strong> {modalDetails.roi}%
              </p>
              <p>
                <strong>Close Cash:</strong>{" "}
                {formatCash(modalDetails.close_cash)}
              </p>
              <p>
                <strong>Open Cash:</strong> {formatCash(modalDetails.open_cash)}
              </p>
            </div>
          </div>
        </div>
      )}
      {dailyPNLData.length === 0 && <p>No P/L</p>}
    </div>
  );
};

export default Calender;
