import React, { useEffect, useRef, useState } from "react";
import "./styles/accountGraph.css";

const AccountGraph = () => {
  const chartRef = useRef(null); // Reference to the canvas element
  const [timePeriod, setTimePeriod] = useState("day"); // State to track the selected time period
  const [topProfitableTrades, setTopProfitableTrades] = useState([]); // State to store top profitable trades

  useEffect(() => {
    // Fetch top profitable trades from API
    const fetchTopProfitableTrades = () => {
      fetch("http://localhost:8000/trades/top-profitable/")
        .then((response) => response.json())
        .then((data) => setTopProfitableTrades(data))
        .catch((error) =>
          console.error("Error fetching top profitable trades:", error)
        );
    };

    fetchTopProfitableTrades();
  }, []); // Fetch data only once on component mount

  useEffect(() => {
    // Load Chart.js from the CDN dynamically
    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/chart.js";
    script.async = true;
    script.onload = () => {
      // Initialize Chart.js instance after the script has loaded
      const ctx = chartRef.current.getContext("2d");
      const myChart = new window.Chart(ctx, {
        type: "line",
        data: {
          labels: getLabels(timePeriod), // Get labels based on the selected time period
          datasets: [
            {
              data: getData(timePeriod), // Get data based on the selected time period
              borderColor: "green", // Green color for stock price line
              backgroundColor: "rgba(0, 0, 0, 0)", // Transparent background color
              borderWidth: 2, // Width of the line
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: false, // Hide legend
            },
          },
          scales: {
            x: {
              grid: {
                display: false, // Hide grid lines
              },
              ticks: {
                display: false, // Hide ticks
              },
              min: 0, // Start y-axis from 0
              max: 1000, // Set max value of y-axis
              position: "center",
            },
            y: {
              display: false, // Hide y-axis
            },
          },
        },
      });

      return () => {
        myChart.destroy(); // Clean up chart on unmount
      };
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script); // Clean up script tag on unmount
    };
  }, [timePeriod, topProfitableTrades]); // Re-render the chart when the time period changes

  // Function to get labels based on the selected time period
  const getLabels = (period) => {
    // Implement logic to return labels based on the selected time period
    // For example, if period is "day", return labels for hours of the day
    if (period === "day") {
      // Return labels for hours of the day
      return ["12AM", "3AM", "6AM", "9AM", "12PM", "3PM", "6PM", "9PM"];
    } else if (period === "weeks") {
      // Return labels for days of the week
      return ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
    } else if (period === "months") {
      // Return labels for days of the month
      // Implement logic to get days of the month
    }
    // Implement labels for other time periods
  };

  // Function to get data based on the selected time period
  const getData = (period) => {
    // Implement logic to return data based on the selected time period
    // For example, if period is "weeks", return data for weeks
  };

  // Function to handle time period change
  const handleTimePeriodChange = (period) => {
    setTimePeriod(period);
  };

  return (
    <div className="account-graph">
      <canvas ref={chartRef} /> {/* Canvas for rendering the chart */}
      <div className="time-period-selector">
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("days")}
        >
          1D
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("weeks")}
        >
          1W
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("months")}
        >
          1M
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("3-months")}
        >
          3M
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("YTD")}
        >
          YTD
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("1-year")}
        >
          1Y
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodChange("max")}
        >
          MAX
        </button>
      </div>
    </div>
  );
};

export default AccountGraph;
