import React, { useState } from "react";
import "./styles/recentTrades.css";
import { showSuccessModal, showUnsuccessModal } from "./Utility"; // Import utility functions

const RecentTrades = ({ recentTrades, formatDate, onRefresh }) => {
  const [selectedTrade, setSelectedTrade] = useState(null);
  const [editedTrade, setEditedTrade] = useState(null);

  const handleRefresh = () => {
    onRefresh();
  };

  const roundStrikePrice = (strikePrice) => {
    return Math.round(parseFloat(strikePrice) * 10) / 10;
  };

  const handleRowClick = (trade) => {
    setSelectedTrade(trade);
    setEditedTrade({ ...trade });
  };

  const handleCloseModal = () => {
    setSelectedTrade(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedTrade((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/trades/${editedTrade.trade_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(editedTrade),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update trade data");
      }
      handleCloseModal();
      onRefresh();
      showSuccessModal(); // Show success modal
    } catch (error) {
      console.error("Error updating trade data:", error);
      //Create form checking here.
      handleCloseModal();
      showUnsuccessModal(error.message); // Show the error modal with the error message
    }
  };

  return (
    <div className="recent-trades">
      <div className="card">
        <p className="recent">Recent Trades</p>
        <i
          className="fa-solid fa-arrows-rotate"
          id="refresh-btn"
          onClick={handleRefresh}
        ></i>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Open Date</th>
            <th>Symbol</th>
            <th>Stock Price</th>
            <th>Expiration Date</th>
            <th>Strike Price</th>
            <th>Type</th>
            <th>Entry Price</th>
            <th>Contracts</th>
            <th>Close Date</th>
            <th>Exit Price</th>
            <th>P/L</th>
          </tr>
        </thead>

        <tbody>
          {recentTrades.map((trade) => (
            <tr key={trade.trade_id} onClick={() => handleRowClick(trade)}>
              <td>{trade.trade_id}</td>
              {/* <td>{formatDate(trade.open_date)}</td> */}
              <td>
                {trade.option_symbol}
                {formatDate(trade.expiration_date)} {trade.option_type}
              </td>
              {/* <td>${trade.stock_price}</td> */}
              {/* <td>{formatDate(trade.expiration_date)}</td>
              <td>${roundStrikePrice(trade.strike_price)}</td> */}
              {/* <td>{trade.option_type}</td> */}
              <td>${trade.entry_price}</td>
              <td>{trade.contracts}</td>
              <td>{trade.close_date ? formatDate(trade.close_date) : ""}</td>
              <td>{trade.exit_price ? `$${trade.exit_price}` : ""}</td>
              <td>${(trade.profit_loss * 100 * trade.contracts).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedTrade && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>
              &times;
            </span>
            <div className="card">
              <h1>{selectedTrade.option_symbol}</h1>
            </div>
            <form onSubmit={handleFormSubmit}>
              <div className="form-group">
                <label htmlFor="option_symbol">Option Symbol:</label>
                <input
                  type="text"
                  name="option_symbol"
                  value={editedTrade.option_symbol}
                  onChange={handleInputChange}
                />{" "}
                <label htmlFor="option_type">Option Type:</label>
                <input
                  type="text"
                  name="option_type"
                  value={editedTrade.option_type}
                  onChange={handleInputChange}
                />{" "}
                <label htmlFor="strike_price">Strike Price:</label>
                <input
                  type="text"
                  name="strike_price"
                  value={editedTrade.strike_price}
                  onChange={handleInputChange}
                />{" "}
                <label htmlFor="strike_price">Entry Price:</label>
                <input
                  type="text"
                  name="entry_price"
                  value={editedTrade.entry_price}
                  onChange={handleInputChange}
                />{" "}
                <label htmlFor="exit_price">Exit Price:</label>
                <input
                  type="text"
                  name="exit_price"
                  placeholder="Exit trade before entering exit price"
                  value={editedTrade.exit_price || ""}
                  onChange={handleInputChange}
                />
                <label htmlFor="contacts">Contracts:</label>
                <input
                  type="text"
                  name="contracts"
                  value={editedTrade.contracts}
                  onChange={handleInputChange}
                />
                <label htmlFor="close_date">Close Date:</label>
                <input
                  type="date"
                  name="close_date"
                  value={editedTrade.close_date || ""}
                  onChange={handleInputChange}
                />
                <label htmlFor="rationale">Rationale:</label>
                <input
                  type="text"
                  name="rationale"
                  value={editedTrade.rationale}
                  onChange={handleInputChange}
                />{" "}
                <label htmlFor="feedback">Feedback:</label>
                <input
                  type="text"
                  name="feedback"
                  value={editedTrade.feedback}
                  onChange={handleInputChange}
                />{" "}
                <label htmlFor="outcome">Outcome:</label>
                <input
                  type="text"
                  name="outcome"
                  value={editedTrade.outcome}
                  onChange={handleInputChange}
                />
                <input type="submit" value="Submit" />
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default RecentTrades;
