import React, { useState, useEffect } from "react";
import Cash from "../controllers/cash/Cash"; // Adjust the path if needed

const Accounts = () => {
  const [userId, setUserId] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch userId from localStorage or other methods if needed
    const token = localStorage.getItem("access_token");
    if (token) {
      try {
        const decodedToken = JSON.parse(localStorage.getItem("user_data"));
        if (decodedToken && decodedToken.sub) {
          setUserId(decodedToken.sub);
        } else {
          throw new Error("Invalid token format");
        }
      } catch (e) {
        console.error("Error decoding token", e);
        setError("Unable to retrieve user data. Please log in again.");
      }
    } else {
      setError("No token found. Please log in.");
    }
  }, []);

  return (
    <div className="Accounts">
      <div>
        <h1>Welcome to Your Account</h1>
        <p>
          This is your user dashboard where you can manage your account settings
          and view your trading activity.
        </p>
        {error && <p className="error">{error}</p>}
        {userId && <Cash userId={userId} />}
      </div>
    </div>
  );
};

export default Accounts;
