import React, { useEffect, useState } from "react";
import {
  FaMoneyBillWave,
  FaChartLine,
  FaCashRegister,
  FaPercentage,
  FaChartPie,
} from "react-icons/fa";
import { getCash } from "../api/CashApi";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { fetchTransactions } from "../api/TransactionsAPI";
import {
  calculateInitialCash,
  calculateAvailableCash,
  calculateNetPL,
  calculateCashBalance,
  calculateROI,
} from "./CashCalc";

const Cash = () => {
  const [cashData, setCashData] = useState(null);
  const [dailyPnls, setDailyPnls] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [cashResponse, pnlResponse, transactionsResponse] =
          await Promise.all([getCash(), fetchDailyPnls(), fetchTransactions()]);

        setCashData(cashResponse);
        setDailyPnls(pnlResponse);
        setTransactions(transactionsResponse);
      } catch (error) {
        setError("No cash data");
        console.error("No cash data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  // Use the new calculations

  // const initialCash = cashData ? calculateInitialCash(cashData) : "NA";
  // const netPL = dailyPnls ? calculateNetPL(dailyPnls) : "NA";
  // const availableCash =
  //   initialCash || transactions
  //     ? calculateAvailableCash(initialCash, transactions)
  //     : "NA";
  // const cashBalance =
  //   initialCash || netPL || transactions
  //     ? calculateCashBalance(initialCash, netPL, transactions)
  //     : "NA";
  // const roi = initialCash && netPL ? calculateROI(initialCash, netPL) : "NA";

  // Ensure valid data with a fallback of 0 to keep calculations valid
  const initialCash = cashData ? calculateInitialCash(cashData) : 0;
  const netPL = dailyPnls.length > 0 ? calculateNetPL(dailyPnls) : 0;

  // Available cash and cash balance should always be calculated, even if initialCash or netPL is 0
  const availableCash = calculateAvailableCash(initialCash, transactions);
  const cashBalance = calculateCashBalance(initialCash, netPL, transactions);

  // Only calculate ROI if initialCash is greater than 0, otherwise it doesn't make sense to calculate
  const roi = initialCash > 0 ? calculateROI(initialCash, netPL) : 0;

  return (
    <div className="cash-container">
      <div className="cash-card-container">
        {/* Initial Cash Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaCashRegister className="card-icon" />
            <span className="label">Initial Cash:</span>
            <span className="value">{`$${initialCash.toFixed(2)}`}</span>
          </div>
        </div>

        {/* Available Cash Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaChartPie className="card-icon" />
            <span className="label">Available Cash:</span>
            <span className="value">{`$${availableCash.toFixed(2)}`}</span>
          </div>
        </div>

        {/* Net P/L Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Net P/L:</span>
            <span className="value">{`$${netPL.toFixed(2)}`}</span>
          </div>
        </div>

        {/* Cash Balance Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Cash Balance:</span>
            <span className="value">{`$${cashBalance.toFixed(2)}`}</span>
          </div>
        </div>

        {/* RoI Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">RoI:</span>
            <span className="value">{`${roi.toFixed(2)}%`}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cash;

