import React, { useState, useEffect } from "react";
import "./styles/styles.css";

const Cash = () => {
  // State to store the fetched cash information
  const [cash, setCash] = useState(null);
  // State to store the fetched analysis data
  const [analysis, setAnalysis] = useState(null);
  // State to indicate if data is loading
  const [loading, setLoading] = useState(true);
  // State to store any error that occurs during fetching
  const [error, setError] = useState(null);

  // Function to fetch cash information from the backend
  const fetchCash = async () => {
    try {
      // Fetch cash data from your backend API
      const response = await fetch("http://localhost:8000/cash");
      if (!response.ok) {
        throw new Error("Failed to fetch cash data");
      }
      const data = await response.json();
      // Set the fetched cash data in state
      setCash(data);
      // Set loading to false after fetching data
      setLoading(false);
    } catch (error) {
      // Set the error state if fetching fails
      setError(error.message);
      // Set loading to false in case of error
      setLoading(false);
    }
  };

  // Function to fetch analysis data from the backend
  const fetchAnalysis = async () => {
    try {
      // Fetch analysis data from your backend API
      const response = await fetch("http://localhost:8000/analysis");
      if (!response.ok) {
        throw new Error("Failed to fetch analysis data");
      }
      const data = await response.json();
      // Set the fetched analysis data in state
      setAnalysis(data);
    } catch (error) {
      // Set the error state if fetching fails
      setError(error.message);
    }
  };

  // UseEffect hook to fetch cash data when component mounts
  useEffect(() => {
    fetchCash();
    fetchAnalysis();
  }, []);

  // Render loading state if data is still loading
  if (loading || !analysis) {
    return <p>Loading cash information...</p>;
  }

  // Render error state if an error occurred during fetching
  if (error) {
    return <p>Error fetching cash data: {error}</p>;
  }

  // Extract current_cash from analysis data
  const currentCash = analysis.current_cash;

  // Render the fetched cash data
  return (
    <div className="cash">
      <div className="header">Cash</div>
      {cash.cash_entries.map((entry, index) => (
        <div className="details" key={index}>
          <p className="text">Initial: ${entry[0]}.00</p>
          <div style={{ width: "10%" }}>
            <br></br>
            <hr></hr>
            <br></br>
          </div>
          <p className="text">Current: ${currentCash}.00</p>
        </div>
      ))}
    </div>
  );
};

export default Cash;
