import { getCash } from "../api/CashApi";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { fetchTrades } from "../api/TradesAPI";
// Import additional APIs if needed
// import { fetchTransactions } from "../api/TransactionsAPI";
// import { fetchWatchlists } from "../api/WatchlistApi";

import {
  calculateSharpeRatio,
  calculateTotalROI,
  calculateNumberOfTrades,
  // calculateTotalNetPL,
  calculateCashBalance,
  calculateWinningTrades,
  calculateLosingTrades,
  calculateWinRate,
  calculateAverageWin,
  calculateAverageLoss,
  calculateRiskRewardRatio,
  calculateMaximumDrawdown,
  // Import any other calculation functions as needed
} from "./AnalysisCalc";

export const performAnalysis = async () => {
  try {
    // Fetch necessary data from the APIs
    const cashData = await getCash();
    const dailyPnls = await fetchDailyPnls();
    const trades = await fetchTrades();
    // Fetch additional data if needed
    // const transactions = await fetchTransactions();
    // const watchlists = await fetchWatchlists();

    console.log(cashData);
    console.log(dailyPnls);
    console.log(trades);

    // Calculate metrics
    const totalROI = calculateTotalROI(trades);
    const numberOfTrades = calculateNumberOfTrades(trades);
    const sharpeRatio = calculateSharpeRatio(dailyPnls);
    // const netPL = calculateTotalNetPL(trades);
    // const cashBalance = calculateCashBalance(cashData.initial_cash, netPL);
    const winningTrades = calculateWinningTrades(trades);
    const losingTrades = calculateLosingTrades(trades);
    const winRate = calculateWinRate(trades);
    const averageWin = calculateAverageWin(trades);
    const averageLoss = calculateAverageLoss(trades);
    const riskRewardRatio = calculateRiskRewardRatio(trades);
    const maximumDrawdown = calculateMaximumDrawdown(dailyPnls);

    console.log(totalROI);
    // console.log(netPL);
    // console.log(cashBalance);
    console.log(sharpeRatio);
    // Prepare the results object
    const analysisResults = {
      totalROI,
      numberOfTrades,
      sharpeRatio,
      // netPL,
      cashBalance,
      winningTrades,
      losingTrades,
      winRate,
      averageWin,
      averageLoss,
      riskRewardRatio,
      maximumDrawdown,
      // Add more metrics as needed
    };
    console.log(analysisResults);

    return analysisResults;
  } catch (error) {
    console.error("Analysis Error:", error);
    throw error;
  }
};
