// src/components/Analysis.js

import React, { useEffect, useState } from "react";
import { analysisAPI } from "../api/AnalysisAPI";

const AnalysisGet = ({ userId }) => {
  const [analysisData, setAnalysisData] = useState(null);
  const [error, setError] = useState("");

  const startDate = "2024-01-01";
  const endDate = "2024-31-01";

  useEffect(() => {
    const fetchAnalysis = async () => {
      try {
        const data = await analysisAPI(userId, startDate, endDate);
        setAnalysisData(data);
      } catch (error) {
        setError("Failed to fetch analysis data");
      }
    };

    fetchAnalysis();
  }, [userId]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!analysisData) {
    return <div>Loading analysis data...</div>;
  }

  return (
    <div className="analysis-container">
      <h2>Trading Analysis</h2>
      <p>Total Trades: {analysisData.total_trades}</p>
      <p>Total Profit/Loss: ${analysisData.total_profit_loss}</p>
      <p>Total ROI: {analysisData.total_roi}%</p>
      <p>Total Deposits: ${analysisData.total_deposits}</p>
      <p>Total Withdrawals: ${analysisData.total_withdrawals}</p>
      <p>Starting Cash: ${analysisData.starting_cash}</p>
      <p>Ending Cash Balance: ${analysisData.ending_cash_balance}</p>
      <p>Average Trade Size: {analysisData.average_trade_size}</p>
      <p>Average Trade Duration: {analysisData.average_trade_duration} days</p>
      <p>Profit Factor: {analysisData.profit_factor}</p>
      <p>Max Drawdown: ${analysisData.max_drawdown}</p>
      <p>Win Rate: {analysisData.win_rate}%</p>
      <p>Largest Win: ${analysisData.largest_win}</p>
      <p>Largest Loss: ${analysisData.largest_loss}</p>
      <p>
        Average Profit/Loss per Trade: $
        {analysisData.average_profit_loss_per_trade}
      </p>
      <p>Cumulative Return: {analysisData.cumulative_return}%</p>
    </div>
  );
};

export default AnalysisGet;
