// src/api/AnalysisAPI.js

const API_BASE_URL = "http://localhost:8000"; // Ensure this is the correct URL for your API

export const analysisAPI = async (startDate, endDate) => {
  try {
    // Retrieve the user ID from local storage or any other secure storage
    const userId = localStorage.getItem("userId");

    if (!userId) {
      throw new Error("User ID not found. Please log in again.");
    }

    const response = await fetch(
      `${API_BASE_URL}/analysis/${userId}?start_date=${startDate}&end_date=${endDate}`,
      {
        method: "GET",
        headers: {
          Accept: "application/json",
        },
      }
    );

    console.log(response);

    if (!response.ok) throw new Error("Error fetching analysis");
    return await response.json();
  } catch (error) {
    console.error("Fetch Analysis Error:", error);
    throw error;
  }
};
