import React, { useState, useEffect } from "react";
import Navigation from "./components/Navigation";
import Account from "./components/Account";
import NewTrade from "./components/NewTrade";
import RecentTrades from "./components/RecentTrades";
import BiggestWinsLosses from "./components/BiggestWinsLosses";
import AccountGraph from "./components/AccountGraph";
import Watchlist from "./components/Watchlist";
import "./components/styles/home.css";

import {
  formatDate,
  showSuccessModal,
  showUnsuccessModal,
  fetchRecentTrades,
  handleNewTradeSubmit,
  handleRefresh,
} from "./components/Utility";

const Home = () => {
  const [recentTrades, setRecentTrades] = useState([]);
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    fetchRecentTrades(setRecentTrades, showSuccessModal, showUnsuccessModal);
    fetchWatchlistData();
  }, []);

  const fetchWatchlistData = async () => {
    try {
      const response = await fetch("http://localhost:8000/watchlist");
      if (!response.ok) {
        throw new Error("Failed to fetch watchlist data");
      }
      const data = await response.json();
      setWatchlist(data.watchlist_entries);
    } catch (error) {
      console.error("Error fetching watchlist data:", error.message);
    }
  };

  return (
    <div>
      <div className="navigation-home">
        <Navigation />
      </div>
      <div className="home">
        <div className="account-home">
          <Account trades={recentTrades} />
        </div>
        <div className="graph-home">
          <AccountGraph trades={recentTrades} />
        </div>
        <div className="winloss-home">
          <BiggestWinsLosses recentTrades={recentTrades} />
        </div>
        <div className="newtrade-home">
          <NewTrade
            onTradeSubmit={(formData) =>
              handleNewTradeSubmit(
                formData,
                fetchRecentTrades,
                showSuccessModal,
                showUnsuccessModal
              )
            }
          />
        </div>
        <div className="trades-home">
          <RecentTrades
            recentTrades={recentTrades}
            formatDate={formatDate}
            onRefresh={() =>
              handleRefresh(
                fetchRecentTrades,
                showSuccessModal,
                showUnsuccessModal
              )
            }
          />
        </div>
        {/* <div className="watchlist-home">
          <Watchlist watchlist={watchlist} setWatchlist={setWatchlist} />
        </div> */}
      </div>
    </div>
  );
};

export default Home;

