// Plan.js
import React, { useEffect, useState } from "react";
import { fetchPlan } from "./ApiCalls";
import { successModal, unsuccessModal } from "./Utility";

const Plan = () => {
  const [plans, setPlans] = useState([]);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    category: "",
    plan: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (showEditModal) {
      setSelectedPlan({ ...selectedPlan, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const fetchPlanData = async () => {
    try {
      const planData = await fetchPlan();
      if (Array.isArray(planData)) {
        setPlans(planData);
        successModal();
      } else {
        throw new Error("Fetched data is not an array");
      }
    } catch (error) {
      setError(error.message);
      unsuccessModal(error.message);
    }
  };

  useEffect(() => {
    fetchPlanData();
  }, []);

  const handleItemClick = (plan) => {
    setSelectedPlan(plan);
    setShowEditModal(true);
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    const [year, month, day] = dateStr.split("-");
    return `${year}-${month}-${day}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedData = {
        ...formData,
        dates: formatDate(formData.dates),
      };
      const response = await fetch("http://localhost:8000/plan/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formattedData),
      });

      if (!response.ok) {
        throw new Error("Failed to add Plan");
      }
      successModal();
      setShowModal(false);
      setFormData({
        dates: "",
        category: "",
        plan: "",
      });
      fetchPlanData();
    } catch (error) {
      console.log("Error adding Plan: ", error);
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedPlan = {
        id: selectedPlan.id,
        dates: formatDate(selectedPlan.dates),
        category: selectedPlan.category,
        plan: selectedPlan.plan,
      };
      const response = await fetch(
        `http://localhost:8000/plan/${selectedPlan.id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formattedPlan),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update Plan");
      }
      successModal();
      setShowEditModal(false);
      fetchPlanData();
    } catch (error) {
      console.log("Error updating Plan: ", error);
    }
  };
  const getFormattedDate = (dateString) => {
    const [year, month, day] = dateString.split("-");
    const date = new Date(year, month - 1, day); // Month is 0-indexed in JS Date
    const daysOfWeek = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    const dayName = daysOfWeek[date.getDay()];
    const formattedDate = `${month}/${day}`; // Format month/day
    return `${dayName}, ${formattedDate}`;
  };

  const formatText = (text) => {
    return text.split("|").map((line, index) => (
      <React.Fragment key={index}>
        <span className="line">{line}</span>
        <br />
      </React.Fragment>
    ));
  };
  return (
    <div className="plan">
      <div className="header">
        <p>Plans</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {plans.map((plan, index) => (
            <li
              key={index}
              className="card-content"
              onClick={() => handleItemClick(plan)}
            >
              <div className="left">
                <p className="category">{plan.category}</p>
                <p className="plan">{formatText(plan.plan)}</p>
                <p className="dates">{getFormattedDate(plan.dates)}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <p>Plan</p>
              <span className="close" onClick={() => setShowModal(false)}>
                &times;
              </span>
            </div>
            <form className="new-trade-form" onSubmit={handleSubmit}>
              <label htmlFor="dates">Date:</label>
              <input
                type="date"
                name="dates"
                value={formData.dates}
                onChange={handleChange}
              />

              <label htmlFor="category">Category:</label>
              <input
                type="text"
                name="category"
                value={formData.category}
                onChange={handleChange}
              />

              <label htmlFor="plan">Plan:</label>
              <input
                type="text"
                name="plan"
                value={formData.plan}
                onChange={handleChange}
              />

              <button type="submit">Add</button>
            </form>
          </div>
        </div>
      )}
      {showEditModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              {selectedPlan.category}
              <span className="close" onClick={() => setShowEditModal(false)}>
                &times;
              </span>
            </div>
            <form className="edit-trade-form" onSubmit={handleEditSubmit}>
              {/* <label htmlFor="dates">Date:</label>
              <input
                type="date"
                name="dates"
                value={selectedPlan.dates}
                onChange={handleChange}
              />

              <label htmlFor="category">Category:</label>
              <input
                type="text"
                name="category"
                value={selectedPlan.category}
                onChange={handleChange}
              /> */}

              <label htmlFor="plan">Plan:</label>
              <input
                type="text"
                name="plan"
                value={selectedPlan.plan}
                onChange={handleChange}
              />

              <button type="submit">Update</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Plan;
