// trading-app/trading-frontend/src/components/controllers/trades/TradeTable.js

import React, { useEffect, useState } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { formatTradeDate } from "../func/functions"; // Importing the functions

const TradeTable = () => {
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getTrades = async () => {
      try {
        const data = await fetchTrades();
        setTrades(data);
        console.log(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getTrades();
  }, []); // Run only once on component mount

  if (loading) {
    return <div>Loading trades...</div>;
  }

  if (error) {
    return <div>Error fetching trades: {error}</div>;
  }

  return (
    <div className="trade-table-container">
      <div className="header-card">
        <p className="title">Trades</p>
        <i className="fa-solid fa-plus" id="new-btn"></i>
      </div>
      <hr />
      <table className="trade-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Pos.</th>
            <th>Date</th>
            <th>Price</th>
            <th>QTY</th>
            <th>Principal</th>
            <th>Exit Date</th>
            <th>Exit Price</th>
            <th>Net</th>
            <th>RoI</th>
          </tr>
        </thead>
        <hr />
        <tbody>
          {trades.map((trade) => (
            <tr key={trade.id}>
              <td>{trade.id}</td>
              <td>
                {trade.symbol} ${trade.strike_price} {trade.option_type}{" "}
                {formatTradeDate(trade.exp_date)}
              </td>
              <td>{formatTradeDate(trade.entry_date)}</td>
              <td>${trade.entry_price}</td>
              <td>{trade.contracts}</td>
              <td>${(trade.principal * 100).toFixed(2)}</td>
              <td>{formatTradeDate(trade.close_date)}</td>
              <td>${trade.exit_price}</td>
              <td>${(trade.net * 100).toFixed(2)}</td>
              <td>{trade.roi * 100}%</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TradeTable;
