import React, { useState } from "react";
import "./styles/biggestWinsLosses.css";
import flip from "./assets/switch.png";

const BiggestWinsLosses = ({ recentTrades }) => {
  const [showPositive, setShowPositive] = useState(true);

  const toggleProfitDisplay = () => {
    setShowPositive(!showPositive);
  };

  const filteredTrades = recentTrades.filter((trade) => {
    if (
      trade.profit_loss === undefined ||
      trade.profit_loss === "" ||
      isNaN(trade.profit_loss) ||
      isNaN(trade.contracts)
    ) {
      return false;
    }
    return showPositive ? trade.profit_loss > 0 : trade.profit_loss < 0;
  });

  // Sort trades in descending order based on profit_loss
  filteredTrades.sort((a, b) => {
    return showPositive
      ? b.profit_loss * b.contracts - a.profit_loss * a.contracts
      : a.profit_loss * a.contracts - b.profit_loss * b.contracts;
  });

  return (
    <div className="wins-lost">
      <div className="card">
        <p>P/L</p>
        <p className="menu" onClick={toggleProfitDisplay}>
          <img src={flip}></img>
        </p>
      </div>
      <div className="card-list">
        <ul className="listed-cards">
          {filteredTrades.map((trade, index) => {
            const isPositive = trade.profit_loss > 0;
            const cardItemClasses = `card-item ${
              isPositive ? "positive" : "negative"
            }`;
            const profitLoss = trade.profit_loss * trade.contracts * 100;

            return (
              <li className={cardItemClasses} key={index}>
                <div className="card-content">
                  <p>{trade.symbol}</p>
                  <p>
                    {/* Calculate and display the percentage difference */}
                    {trade.entry_price && trade.exit_price
                      ? `${(
                          ((trade.exit_price - trade.entry_price) /
                            trade.entry_price) *
                          100
                        ).toFixed(2)}%`
                      : ""}
                  </p>
                  <p>${profitLoss.toFixed(2)}</p>
                </div>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default BiggestWinsLosses;
