//\trading-frontend\src\components\controllers\func\functions.js
export const formatTicker = (ticker) => {
  // Trim any spaces and then pad to the left with spaces until the length is 4
  return ticker.trim().padEnd(4, " ");
};

export const formatTradeDate = (dateStr) => {
  // Ensure the date string is valid
  if (!dateStr || !dateStr.includes("-")) return "";

  const dateParts = dateStr.split("-");
  const month = dateParts[1]; // Extract month
  const day = dateParts[2]; // Extract day

  return `${month}/${day}`; // Return formatted date
};
