import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    return <div>Error fetching analysis data: {error.message}</div>;
  }

  if (!analysisResults) {
    return <div>Loading...</div>;
  }

  const {
    numberOfTrades,
    winningTrades,
    losingTrades,
    winRate,
    averageWin,
    averageLoss,
    riskRewardRatio,
  } = analysisResults;

  const formatValue = (value) => {
    return isNaN(value) || value === undefined ? "0.00" : value.toFixed(2);
  };

  return (
    <div className="analysis-container">
      <div className="analysis-row">
        <div className="analysis-circle">
          <h3 className="labels">Trades</h3>
          <p>{numberOfTrades}</p>
        </div>
        <div className="analysis-circle">
          <h3>Wins</h3>
          <p>{winningTrades}</p>
        </div>
        <div className="analysis-circle">
          <h3>Losses</h3>
          <p>{losingTrades}</p>
        </div>
      </div>

      <div className="analysis-metrics-row">
        <div className="analysis-metric">
          <h3>Win Rate</h3>
          <p>{formatValue(winRate)}%</p>
        </div>
        <div className="analysis-metric">
          <h3>Average Win</h3>
          <p>${formatValue(averageWin)}</p>
        </div>
        <div className="analysis-metric">
          <h3>Average Loss</h3>
          <p>${formatValue(averageLoss)}</p>
        </div>{" "}
        <div className="analysis-metric">
          <h3>Risk Reward</h3>
          <p>${formatValue(riskRewardRatio)}</p>
        </div>
      </div>
    </div>
  );
};

export default AnalysisDisplay;
