import React, { useState, useEffect } from "react";
import "./styles/plan.css";

const Plan = () => {
  const [strategies, setStrategies] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchStrategies();
  }, []);

  const fetchStrategies = async () => {
    try {
      const response = await fetch("http://localhost:8000/strategies/");
      if (!response.ok) {
        throw new Error("Failed to fetch strategies");
      }
      const data = await response.json();
      setStrategies(data);
    } catch (error) {
      console.error("Error fetching strategies:", error.message);
    }
  };

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch("http://localhost:8000/strategies/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add strategy");
      }
      setShowModal(false);
      fetchStrategies();
    } catch (error) {
      console.error("Error adding strategy:", error);
    }
  };

  return (
    <div className="plan">
      <div className="header-card">
        <p>Plan</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="plan-list-container">
        <ul className="cards">
          {strategies.map((strategy) => (
            <li key={strategy.strategy_id} className="list-item">
              <div className="card-summary">
                <div className="category">
                  <h3>Plan:</h3>
                  <p>{strategy.plan}</p>
                </div>
                <div className="category">
                  <h3>Position:</h3>
                  <p>{strategy.position_sizing_rules}</p>
                </div>
                <div className="category">
                  <h3>Outcome:</h3>
                  <p>{strategy.outcome}</p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <Modal onClose={() => setShowModal(false)} onSubmit={handleSubmit} />
      )}
    </div>
  );
};

const Modal = ({ onClose, onSubmit }) => {
  const [formData, setFormData] = useState({ plan: "" });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <form onSubmit={handleSubmit}>
          <label htmlFor="plan">Plan:</label>
          <input
            type="text"
            name="plan"
            value={formData.plan}
            onChange={handleChange}
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default Plan;
