import React, { useState, useEffect } from "react";
import "./styles/watchlist.css"; // Import CSS file
import { formatDate } from "./Utility"; // Import utility functions

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist") // Replace with your backend API URL
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  return (
    <div className="watchlist">
      <div className="header">Watchlist</div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item) => (
            <li key={item.watch_id} className="card-items">
              <div className="top-line">
                <div>Symbol: {item.symbol}</div>
                <div>Exp. Date: {formatDate(item.expiration_date)}</div>
              </div>
              <div className="price-line">
                <div>Price: {item.current_price}</div>
              </div>
              <div className="target-line">
                <div>Target: {item.target_price}</div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Watchlist;
